import React, { useState, useRef } from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 12px;
  padding: 2rem;
  width: 90%;
  max-width: 600px;
  max-height: 90vh;
  overflow-y: auto;
  box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid #e5e7eb;
`;

const ModalTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1f2937;
  margin: 0;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #6b7280;
  padding: 0.5rem;
  border-radius: 4px;
  transition: all 0.2s;

  &:hover {
    background: #f3f4f6;
    color: #374151;
  }
`;

const UploadSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const FileUploadArea = styled.div<{ isDragOver: boolean; hasFile: boolean }>`
  border: 2px dashed ${props => props.isDragOver ? '#2563eb' : props.hasFile ? '#10b981' : '#d1d5db'};
  border-radius: 8px;
  padding: 2rem;
  text-align: center;
  background: ${props => props.isDragOver ? '#eff6ff' : props.hasFile ? '#f0fdf4' : '#f9fafb'};
  transition: all 0.2s ease;
  cursor: pointer;

  &:hover {
    border-color: #2563eb;
    background: #eff6ff;
  }
`;

const UploadIcon = styled.div`
  font-size: 3rem;
  color: #6b7280;
  margin-bottom: 1rem;
`;

const UploadText = styled.p`
  font-size: 1rem;
  color: #374151;
  margin-bottom: 0.5rem;
`;

const UploadSubtext = styled.p`
  font-size: 0.875rem;
  color: #6b7280;
  margin: 0;
`;

const FileInput = styled.input`
  display: none;
`;

const FileInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem;
  background: #f0fdf4;
  border: 1px solid #10b981;
  border-radius: 6px;
  margin-top: 1rem;
`;

const FileIcon = styled.span`
  font-size: 1.5rem;
  color: #10b981;
`;

const FileName = styled.span`
  font-size: 0.875rem;
  color: #065f46;
  font-weight: 500;
`;

const FileSize = styled.span`
  font-size: 0.75rem;
  color: #6b7280;
`;

const TemplateSection = styled.div`
  border: 1px solid #e5e7eb;
  border-radius: 8px;
  padding: 1rem;
  background: #f9fafb;
`;

const TemplateTitle = styled.h3`
  font-size: 1rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.5rem;
`;

const TemplateDescription = styled.p`
  font-size: 0.875rem;
  color: #6b7280;
  margin-bottom: 1rem;
`;

const TemplateButton = styled.button`
  background: #2563eb;
  color: white;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 6px;
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background: #1d4ed8;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1.5rem;
`;

const Button = styled.button`
  background: #2563eb;
  color: white;
  padding: 1rem 2rem;
  border-radius: 6px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background: #1d4ed8;
  }

  &:disabled {
    background: #9ca3af;
    cursor: not-allowed;
  }
`;

const CancelButton = styled.button`
  background: #6b7280;
  color: white;
  padding: 1rem 2rem;
  border-radius: 6px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background: #4b5563;
  }
`;

const ProgressSection = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background: #f0fdf4;
  border: 1px solid #10b981;
  border-radius: 6px;
  display: none;

  &.visible {
    display: block;
  }
`;

const ProgressText = styled.p`
  font-size: 0.875rem;
  color: #065f46;
  margin: 0;
`;

const AddressSearchSection = styled.div`
  border: 1px solid #e5e7eb;
  border-radius: 8px;
  padding: 1rem;
  background: #f9fafb;
  margin-bottom: 1rem;
`;

const AddressSearchTitle = styled.h3`
  font-size: 1rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.5rem;
`;

const AddressSearchDescription = styled.p`
  font-size: 0.875rem;
  color: #6b7280;
  margin-bottom: 1rem;
`;

const AddressSearchInput = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 0.875rem;
  margin-bottom: 0.5rem;

  &:focus {
    outline: none;
    border-color: #2563eb;
    box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
  }
`;

const AddressSearchButton = styled.button`
  background: #2563eb;
  color: white;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 6px;
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background: #1d4ed8;
  }

  &:disabled {
    background: #9ca3af;
    cursor: not-allowed;
  }
`;

const CoordinateResult = styled.div`
  margin-top: 0.5rem;
  padding: 0.5rem;
  background: #f0fdf4;
  border: 1px solid #10b981;
  border-radius: 4px;
  font-size: 0.75rem;
  color: #065f46;
`;

interface AddPropertyModalProps {
  onClose: () => void;
}

const AddPropertyModal: React.FC<AddPropertyModalProps> = ({ onClose }) => {
  const [isDragOver, setIsDragOver] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Ï£ºÏÜåÍ≤ÄÏÉâ Í¥ÄÎ†® ÏÉÅÌÉú
  const [addressSearch, setAddressSearch] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [searchResult, setSearchResult] = useState<{ lat: number; lng: number } | null>(null);

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' || 
          file.type === 'application/vnd.ms-excel' ||
          file.name.endsWith('.xlsx') || 
          file.name.endsWith('.xls')) {
        setSelectedFile(file);
      } else {
        alert('ÏóëÏÖÄ ÌååÏùº(.xlsx, .xls)Îßå ÏóÖÎ°úÎìú Í∞ÄÎä•Ìï©ÎãàÎã§.');
      }
    }
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      const file = files[0];
      if (file.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' || 
          file.type === 'application/vnd.ms-excel' ||
          file.name.endsWith('.xlsx') || 
          file.name.endsWith('.xls')) {
        setSelectedFile(file);
      } else {
        alert('ÏóëÏÖÄ ÌååÏùº(.xlsx, .xls)Îßå ÏóÖÎ°úÎìú Í∞ÄÎä•Ìï©ÎãàÎã§.');
      }
    }
  };

  const handleUploadClick = () => {
    fileInputRef.current?.click();
  };

  const downloadTemplate = () => {
    // ÏóëÏÖÄ ÌÖúÌîåÎ¶ø Îã§Ïö¥Î°úÎìú Î°úÏßÅ
    const templateData = [
      ['Îß§Î¨ºÎ≤àÌò∏', 'Îß§Î¨ºÏ†úÎ™©', 'Îß§Î¨ºÏÑ§Î™Ö', 'ÌôïÏù∏Îß§Î¨º', 'Ï£ºÏÜå', 'ÏúÑÎèÑ', 'Í≤ΩÎèÑ', 'Îß§Î¨ºÏ¢ÖÎ•ò', 'Í±∞ÎûòÏú†Ìòï', 'Îß§Îß§Í∞Ä', 'Î≥¥Ï¶ùÍ∏à/ÏûÑÎåÄÎ£å', 'Í¥ÄÎ¶¨ÎπÑ', 'ÏúµÏûêÍ∏à', 'Í≥µÍ∏â/Ï†ÑÏö©Î©¥Ï†Å', 'Í±¥Ï∂ïÎ¨ºÏö©ÎèÑ', 'Ìï¥ÎãπÏ∏µ/Ï†ÑÏ≤¥Ï∏µ', 'Î∞©/ÌôîÏû•Ïã§', 'ÎÇúÎ∞©Ïú†Ìòï', 'Ï£ºÏ∞®', 'ÏóòÎ¶¨Î≤†Ïù¥ÌÑ∞', 'ÏÇ¨Ïö©ÏäπÏù∏Ïùº', 'ÏûÖÏ£ºÍ∞ÄÎä•Ïùº'],
      ['P001', 'Í∞ïÎÇ®Íµ¨ Ïã†Ï∂ï ÏïÑÌååÌä∏', 'Í∞ïÎÇ®Íµ¨ Ïã†Ï∂ï ÏïÑÌååÌä∏ÏûÖÎãàÎã§', 'ÌôïÏù∏Îß§Î¨º', 'ÏÑúÏö∏Ïãú Í∞ïÎÇ®Íµ¨', '37.5665', '126.9780', 'ÏïÑÌååÌä∏', 'Îß§Îß§', '5ÏñµÏõê', '', '20ÎßåÏõê', '', '50Ìèâ/45Ìèâ', 'Ï£ºÍ±∞Ïö©', '5Ï∏µ/15Ï∏µ', '3Î∞©2Ïöï', 'Í∞úÎ≥ÑÎÇúÎ∞©', 'Í∞ÄÎä•', 'ÏûàÏùå', '2023-01-01', 'Ï¶âÏãúÏûÖÏ£º'],
      ['P002', 'ÌôçÎåÄÏûÖÍµ¨ Í∑ºÏ≤ò ÏõêÎ£∏', 'ÌôçÎåÄÏûÖÍµ¨ Í∑ºÏ≤ò ÏõêÎ£∏ÏûÖÎãàÎã§', 'ÌôïÏù∏Îß§Î¨º', 'ÏÑúÏö∏Ïãú ÎßàÌè¨Íµ¨', '37.5575', '126.9250', 'ÏõêÎ£∏', 'ÏûÑÎåÄ', '', 'Î≥¥Ï¶ùÍ∏à 1000ÎßåÏõê/ÏõîÏÑ∏ 50ÎßåÏõê', '5ÎßåÏõê', '', '20Ìèâ/18Ìèâ', 'Ï£ºÍ±∞Ïö©', '3Ï∏µ/5Ï∏µ', '1Î∞©1Ïöï', 'Í∞úÎ≥ÑÎÇúÎ∞©', 'Î∂àÍ∞ÄÎä•', 'ÏóÜÏùå', '2020-05-01', 'Ï¶âÏãúÏûÖÏ£º']
    ];

    let csvContent = "data:text/csv;charset=utf-8,\uFEFF";
    templateData.forEach(row => {
      csvContent += row.join(',') + '\r\n';
    });

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement('a');
    link.setAttribute('href', encodedUri);
    link.setAttribute('download', 'Îß§Î¨º_Îì±Î°ù_ÌÖúÌîåÎ¶ø.csv');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('ÌååÏùºÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setIsUploading(true);
    setUploadProgress(0);

    // ÏãúÎÆ¨Î†àÏù¥ÏÖòÎêú ÏóÖÎ°úÎìú ÏßÑÌñâÎ•†
    const interval = setInterval(() => {
      setUploadProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setIsUploading(false);
          alert('Îß§Î¨º Îì±Î°ùÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!');
          onClose();
          return 100;
        }
        return prev + 10;
      });
    }, 200);

    // Ïã§Ï†ú Íµ¨ÌòÑÏóêÏÑúÎäî Ïó¨Í∏∞Ïóê ÌååÏùº ÏóÖÎ°úÎìú API Ìò∏Ï∂ú
    // const formData = new FormData();
    // formData.append('file', selectedFile);
    // const response = await fetch('/api/upload-properties', {
    //   method: 'POST',
    //   body: formData
    // });
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  // Ï£ºÏÜåÍ≤ÄÏÉâÏúºÎ°ú Ï¢åÌëúÎ≥ÄÌôò Ìï®Ïàò
  const searchAddress = async () => {
    if (!addressSearch.trim()) {
      alert('Ï£ºÏÜåÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setIsSearching(true);
    setSearchResult(null);

    try {
      // Google Geocoding API ÏÇ¨Ïö© (Ïã§Ï†ú Íµ¨ÌòÑ Ïãú API ÌÇ§ ÌïÑÏöî)
      const response = await fetch(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(addressSearch)}&key=YOUR_API_KEY`
      );

      if (!response.ok) {
        throw new Error('Ï£ºÏÜå Í≤ÄÏÉâÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }

      const data = await response.json();

      if (data.results && data.results.length > 0) {
        const location = data.results[0].geometry.location;
        setSearchResult({
          lat: location.lat,
          lng: location.lng
        });
      } else {
        alert('Ìï¥Îãπ Ï£ºÏÜåÎ•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.');
      }
    } catch (error) {
      console.error('Ï£ºÏÜå Í≤ÄÏÉâ Ïò§Î•ò:', error);
      
      // API ÌÇ§Í∞Ä ÏóÜÎäî Í≤ΩÏö∞Î•º ÏúÑÌïú ÏãúÎÆ¨Î†àÏù¥ÏÖò
      // Ïã§Ï†ú Íµ¨ÌòÑÏóêÏÑúÎäî Google Geocoding APIÎ•º ÏÇ¨Ïö©Ìï¥Ïïº Ìï©ÎãàÎã§
      const mockResults = {
        'ÏÑúÏö∏Ïãú Í∞ïÎÇ®Íµ¨': { lat: 37.5665, lng: 126.9780 },
        'ÏÑúÏö∏Ïãú ÎßàÌè¨Íµ¨': { lat: 37.5575, lng: 126.9250 },
        'ÏÑúÏö∏Ïãú ÏÑúÏ¥àÍµ¨': { lat: 37.4837, lng: 127.0324 },
        'ÏÑúÏö∏Ïãú ÏÜ°ÌååÍµ¨': { lat: 37.5145, lng: 127.1059 }
      };

      const mockResult = mockResults[addressSearch as keyof typeof mockResults];
      if (mockResult) {
        setSearchResult(mockResult);
        alert('ÏãúÎÆ¨Î†àÏù¥ÏÖò Í≤∞Í≥ºÏûÖÎãàÎã§. Ïã§Ï†ú Íµ¨ÌòÑÏóêÏÑúÎäî Google Geocoding APIÎ•º ÏÇ¨Ïö©Ìï©ÎãàÎã§.');
      } else {
        alert('Ìï¥Îãπ Ï£ºÏÜåÎ•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§. (ÏãúÎÆ¨Î†àÏù¥ÏÖò Î™®Îìú)');
      }
    } finally {
      setIsSearching(false);
    }
  };

  const copyCoordinates = () => {
    if (searchResult) {
      const coordinates = `${searchResult.lat}, ${searchResult.lng}`;
      navigator.clipboard.writeText(coordinates).then(() => {
        alert('Ï¢åÌëúÍ∞Ä ÌÅ¥Î¶ΩÎ≥¥ÎìúÏóê Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!');
      }).catch(() => {
        // ÌÅ¥Î¶ΩÎ≥¥Îìú APIÍ∞Ä ÏßÄÏõêÎêòÏßÄ ÏïäÎäî Í≤ΩÏö∞
        const textArea = document.createElement('textarea');
        textArea.value = coordinates;
        document.body.appendChild(textArea);
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        alert('Ï¢åÌëúÍ∞Ä ÌÅ¥Î¶ΩÎ≥¥ÎìúÏóê Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!');
      });
    }
  };

  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <ModalHeader>
          <ModalTitle>ÎåÄÎüâ Îß§Î¨º Îì±Î°ù</ModalTitle>
          <CloseButton onClick={onClose}>&times;</CloseButton>
        </ModalHeader>

        <UploadSection>
          <FileUploadArea
            isDragOver={isDragOver}
            hasFile={!!selectedFile}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            onClick={handleUploadClick}
          >
            <UploadIcon>üìÅ</UploadIcon>
            <UploadText>
              {selectedFile ? 'ÌååÏùºÏù¥ ÏÑ†ÌÉùÎêòÏóàÏäµÎãàÎã§' : 'ÏóëÏÖÄ ÌååÏùºÏùÑ ÎìúÎûòÍ∑∏ÌïòÍ±∞ÎÇò ÌÅ¥Î¶≠ÌïòÏó¨ ÏóÖÎ°úÎìú'}
            </UploadText>
            <UploadSubtext>
