import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';
import { Property } from '../types';

const MapContainer = styled.div`
  width: 100%;
  height: 100%;
  min-height: 400px;
  background: #f0f0f0;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #666;
  font-size: 1rem;
  position: relative;
  overflow: visible;

  /* 마름모형 기호 완전 제거 */
  .gmnoprint {
    display: none !important;
  }

  /* 지도 타입 컨트롤 완전 제거 */
  .gm-style-mtc {
    display: none !important;
  }

  /* 모든 불필요한 컨트롤 제거 */
  .gm-style button[title*="Map"] {
    display: none !important;
  }
`;

interface GoogleMapProps {
  properties: Property[];
  onMarkerClick?: (property: Property) => void;
  onClusterClick?: (properties: Property[]) => void;
  selectedMarkerId?: string | null;
  setSelectedMarkerId?: (id: string) => void;
  selectedClusterId?: string | null;
  setSelectedClusterId?: (id: string) => void;
}

declare global {
  interface Window {
    google: any;
  }
}

const GoogleMap: React.FC<GoogleMapProps> = ({
  properties,
  onMarkerClick,
  onClusterClick,
  selectedMarkerId,
  setSelectedMarkerId,
  selectedClusterId,
  setSelectedClusterId
}) => {
  const mapRef = useRef<HTMLDivElement>(null);
  const mapInstance = useRef<any>(null);
  const markersRef = useRef<any[]>([]);
  const isClusterClicking = useRef(false);

  // 마커 클러스터링 함수
  const createClusterMarker = (properties: Property[], center: { lat: number; lng: number }) => {
    const clusterId = properties.map(p => p.id).join(',');
    const isSelected = selectedClusterId === clusterId;

    const marker = new window.google.maps.Marker({
      position: center,
      map: mapInstance.current,
      icon: {
        url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(
          isSelected
            ? `<svg width="44" height="44" viewBox="0 0 44 44" fill="none" xmlns="http://www.w3.org/2000/svg">
                <circle cx="22" cy="22" r="20" fill="white" stroke="#111" stroke-width="3"/>
                <text x="22" y="26" text-anchor="middle" fill="#111" font-family="Arial, sans-serif" font-size="14" font-weight="bold">${properties.length}</text>
              </svg>`
            : `<svg width="40" height="40" viewBox="0 0 40 40" fill="none" xmlns="http://www.w3.org/2000/svg">
                <circle cx="20" cy="20" r="18" fill="#2563eb" stroke="#1e40af" stroke-width="3"/>
                <text x="20" y="26" text-anchor="middle" fill="white" font-family="Arial, sans-serif" font-size="14" font-weight="bold">${properties.length}</text>
              </svg>`
        ),
        scaledSize: isSelected
          ? new window.google.maps.Size(44, 44)
          : new window.google.maps.Size(40, 40),
        anchor: isSelected
          ? new window.google.maps.Point(22, 22)
          : new window.google.maps.Point(20, 20)
      }
    });

    // 클러스터 마커 클릭 시 지도 상태 변경 없이 콜백만 실행
    marker.addListener('click', () => {
      console.log(`클러스터 클릭: ${properties.length}개 매물`);

      isClusterClicking.current = true;

      if (properties.length === 1) {
        if (onMarkerClick) {
          onMarkerClick(properties[0]);
        }
      } else {
        if (onClusterClick) {
          onClusterClick(properties);
        }
      }

      setTimeout(() => {
        isClusterClicking.current = false;
      }, 1500);
    });

    // 클러스터 hover 효과 추가
    marker.addListener('mouseover', () => {
      if (!isSelected) {
        marker.setIcon({
          url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
            <svg width="44" height="44" viewBox="0 0 44 44" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="22" cy="22" r="20" fill="white" stroke="#111" stroke-width="3"/>
              <text x="22" y="26" text-anchor="middle" fill="#111" font-family="Arial, sans-serif" font-size="14" font-weight="bold">${properties.length}</text>
            </svg>
          `),
          scaledSize: new window.google.maps.Size(44, 44),
          anchor: new window.google.maps.Point(22, 22)
        });
      }
    });

    marker.addListener('mouseout', () => {
      if (!isSelected) {
        marker.setIcon({
          url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
            <svg width="40" height="40" viewBox="0 0 40 40" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="20" cy="20" r="18" fill="#2563eb" stroke="#1e40af" stroke-width="3"/>
              <text x="20" y="26" text-anchor="middle" fill="white" font-family="Arial, sans-serif" font-size="14" font-weight="bold">${properties.length}</text>
            </svg>
          `),
          scaledSize: new window.google.maps.Size(40, 40),
          anchor: new window.google.maps.Point(20, 20)
        });
      }
    });

    return marker;
  };

  // 마커 클러스터링 로직
  const clusterMarkers = (markers: any[], zoom: number) => {
    const clusters: { center: { lat: number; lng: number }; properties: Property[] }[] = [];

    let clusterRadius: number;
    if (zoom < 8) {
      clusterRadius = 0.05;
    } else if (zoom < 10) {
      clusterRadius = 0.02;
    } else if (zoom < 12) {
      clusterRadius = 0.01;
    } else if (zoom < 14) {
      clusterRadius = 0.005;
    } else if (zoom < 16) {
      clusterRadius = 0.002;
    } else if (zoom < 18) {
      clusterRadius = 0.001;
    } else {
      clusterRadius = 0.00001;
    }

    console.log(`현재 줌 레벨: ${zoom}, 클러스터 반경: ${clusterRadius}`);

    properties.forEach((property, index) => {
      let addedToCluster = false;

      for (const cluster of clusters) {
        const distance = Math.sqrt(
          Math.pow(property.location.lat - cluster.center.lat, 2) +
          Math.pow(property.location.lng - cluster.center.lng, 2)
        );

        if (distance < clusterRadius) {
          cluster.properties.push(property);
          cluster.center = {
            lat: cluster.properties.reduce((sum, p) => sum + p.location.lat, 0) / cluster.properties.length,
            lng: cluster.properties.reduce((sum, p) => sum + p.location.lng, 0) / cluster.properties.length
          };
          addedToCluster = true;
          console.log(`매물 ${property.id}를 기존 클러스터에 추가 (총 ${cluster.properties.length}개)`);
          break;
        }
      }

      if (!addedToCluster) {
        clusters.push({
          center: property.location,
          properties: [property]
        });
        console.log(`새 클러스터 생성: 매물 ${property.id}`);
      }
    });

    console.log(`총 ${clusters.length}개의 클러스터 생성됨`);
    return clusters;
  };

  // 지도 초기화
  useEffect(() => {
    const initMap = () => {
      if (!mapRef.current || !window.google || !window.google.maps) {
        return false;
      }

      if (mapInstance.current) {
        console.log('지도가 이미 초기화됨 - 마커만 업데이트');
        updateMarkers();
        return true;
      }

      try {
        markersRef.current.forEach(marker => {
          if (marker && marker.setMap) {
            marker.setMap(null);
          }
        });
        markersRef.current = [];

        const map = new window.google.maps.Map(mapRef.current, {
          center: { lat: 37.5665, lng: 126.9780 },
          zoom: 10,
          mapTypeId: window.google.maps.MapTypeId.ROADMAP,
          zoomControl: true,
          mapTypeControl: false,
          scaleControl: false,
          streetViewControl: false,
          rotateControl: false,
          fullscreenControl: false,
          gestureHandling: 'greedy',
          mapTypeControlOptions: {
            style: window.google.maps.MapTypeControlStyle.HORIZONTAL_BAR,
            position: window.google.maps.ControlPosition.TOP_RIGHT
          },
          backgroundColor: '#f0f0f0',
          disableDefaultUI: false,
          maxZoom: 20,
          minZoom: 8,
          tilt: 0,
          zoomControlOptions: {
            position: window.google.maps.ControlPosition.RIGHT_TOP
          },
          disableDoubleClickZoom: false,
          restriction: {
            latLngBounds: {
              north: 38.0,
              south: 34.5,
              east: 131.0,
              west: 125.0
            },
            strictBounds: false
          }
        });

        mapInstance.current = map;

        map.addListener('tilesloaded', () => {
          console.log('지도 타일 로드 완료');
        });

        map.addListener('idle', () => {
          console.log('지도 완전 로드됨');
        });

        console.log('지도 초기화 완료');

        createMarkers();

        map.addListener('zoom_changed', () => {
          if (isClusterClicking.current) {
            console.log('클러스터 클릭 중 - 마커 재생성 건너뜀');
            return;
          }

          const newZoom = map.getZoom();
          console.log('줌 레벨 변경됨:', newZoom);

          // 기존 마커 제거
          markersRef.current.forEach(marker => {
            if (marker && marker.setMap) {
              marker.setMap(null);
            }
          });
          markersRef.current = [];

          // 새 마커 생성 (지연 시간으로 안정성 확보)
          setTimeout(() => {
            if (!isClusterClicking.current) {
              try {
                console.log('줌 변경으로 인한 마커 재생성 시작');
                createMarkers();
                console.log('줌 변경으로 인한 마커 재생성 완료');
              } catch (error) {
                console.error('마커 재생성 중 오류:', error);
              }
            }
          }, 300); // 지연 시간을 300ms로 증가
        });

        map.addListener('click', () => {
          if (isClusterClicking.current) {
            console.log('지도 클릭으로 클러스터 클릭 플래그 해제');
            isClusterClicking.current = false;
          }
        });

        map.addListener('dragstart', () => {
          if (isClusterClicking.current) {
            console.log('지도 드래그로 클러스터 클릭 플래그 해제');
            isClusterClicking.current = false;
          }
        });

        return true;
      } catch (error) {
        console.error('지도 초기화 오류:', error);
        return false;
      }
    };

    const createMarkers = () => {
      if (!mapInstance.current) return;

      const currentZoom = mapInstance.current.getZoom();
      console.log(`마커 생성 시작 - 줌 레벨: ${currentZoom}, 매물 수: ${properties.length}`);

      if (currentZoom >= 18) {
        console.log('최대 줌 레벨 - 개별 마커만 생성');
        properties.forEach((property, index) => {
          const isSelected = selectedMarkerId === property.id;
          const marker = new window.google.maps.Marker({
            position: property.location,
            map: mapInstance.current,
            title: property.title,
            icon: {
              url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(
                isSelected
                  ? `<svg width="44" height="44" viewBox="0 0 44 44" fill="none" xmlns="http://www.w3.org/2000/svg"><circle cx="22" cy="22" r="20" fill="white" stroke="#111" stroke-width="3"/><text x="22" y="29" text-anchor="middle" fill="#111" font-family="Arial, sans-serif" font-size="16" font-weight="bold">1</text></svg>`
                  : `<svg width="30" height="30" viewBox="0 0 30 30" fill="none" xmlns="http://www.w3.org/2000/svg"><circle cx="15" cy="15" r="13" fill="#2563eb" stroke="#1e40af" stroke-width="2"/><text x="15" y="19" text-anchor="middle" fill="white" font-family="Arial, sans-serif" font-size="10" font-weight="bold">1</text></svg>`
              ),
              scaledSize: isSelected
                ? new window.google.maps.Size(44, 44)
                : new window.google.maps.Size(30, 30),
              anchor: isSelected
                ? new window.google.maps.Point(22, 22)
                : new window.google.maps.Point(15, 15)
            }
          });

          if (onMarkerClick) {
            marker.addListener('click', () => {
              console.log(`개별 마커 클릭됨: ${property.id} - ${property.title}`);
              isClusterClicking.current = true;

              // 클러스터 선택 해제하고 개별 마커 선택
              if (setSelectedMarkerId) setSelectedMarkerId(property.id);
              if (setSelectedClusterId) setSelectedClusterId('');
              onMarkerClick(property);

              setTimeout(() => {
                isClusterClicking.current = false;
              }, 1500);
            });
          }

          marker.addListener('mouseover', () => {
            if (!isSelected) {
              marker.setIcon({
                url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
                  <svg width="44" height="44" viewBox="0 0 44 44" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <circle cx="22" cy="22" r="20" fill="white" stroke="#111" stroke-width="3"/>
                    <text x="22" y="29" text-anchor="middle" fill="#111" font-family="Arial, sans-serif" font-size="16" font-weight="bold">1</text>
                  </svg>
                `),
                scaledSize: new window.google.maps.Size(44, 44),
                anchor: new window.google.maps.Point(22, 22)
              });
            }
          });

          marker.addListener('mouseout', () => {
            if (!isSelected) {
              marker.setIcon({
                url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
                  <svg width="30" height="30" viewBox="0 0 30 30" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <circle cx="15" cy="15" r="13" fill="#2563eb" stroke="#1e40af" stroke-width="2"/>
                    <text x="15" y="19" text-anchor="middle" fill="white" font-family="Arial, sans-serif" font-size="10" font-weight="bold">1</text>
                  </svg>
                `),
                scaledSize: new window.google.maps.Size(30, 30),
                anchor: new window.google.maps.Point(15, 15)
              });
            }
          });

          markersRef.current.push(marker);
          console.log(`개별 마커 생성: 매물 ${property.id} (${property.location.lat}, ${property.location.lng})`);
        });
      } else {
        const clusters = clusterMarkers([], currentZoom);

        clusters.forEach((cluster, clusterIndex) => {
          console.log(`클러스터 ${clusterIndex + 1}: ${cluster.properties.length}개 매물`);

          if (cluster.properties.length === 1) {
            const property = cluster.properties[0];
            const isSelected = selectedMarkerId === property.id;
            const marker = new window.google.maps.Marker({
              position: property.location,
              map: mapInstance.current,
              title: property.title,
              icon: {
                url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(
                  isSelected
                    ? `<svg width="44" height="44" viewBox="0 0 44 44" fill="none" xmlns="http://www.w3.org/2000/svg"><circle cx="22" cy="22" r="20" fill="white" stroke="#111" stroke-width="3"/><text x="22" y="29" text-anchor="middle" fill="#111" font-family="Arial, sans-serif" font-size="16" font-weight="bold">1</text></svg>`
                    : `<svg width="30" height="30" viewBox="0 0 30 30" fill="none" xmlns="http://www.w3.org/2000/svg"><circle cx="15" cy="15" r="13" fill="#2563eb" stroke="#1e40af" stroke-width="2"/><text x="15" y="19" text-anchor="middle" fill="white" font-family="Arial, sans-serif" font-size="10" font-weight="bold">1</text></svg>`
                ),
                scaledSize: isSelected
                  ? new window.google.maps.Size(44, 44)
                  : new window.google.maps.Size(30, 30),
                anchor: isSelected
                  ? new window.google.maps.Point(22, 22)
                  : new window.google.maps.Point(15, 15)
              }
            });

            if (onMarkerClick) {
              marker.addListener('click', () => {
                console.log(`클러스터링된 단일 마커 클릭됨: ${property.id} - ${property.title}`);
                isClusterClicking.current = true;

                // 클러스터 선택 해제하고 개별 마커 선택
                if (setSelectedMarkerId) setSelectedMarkerId(property.id);
                if (setSelectedClusterId) setSelectedClusterId('');
                onMarkerClick(property);

                setTimeout(() => {
                  isClusterClicking.current = false;
                }, 1500);
              });
            }

            marker.addListener('mouseover', () => {
              if (!isSelected) {
                marker.setIcon({
                  url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
                    <svg width="44" height="44" viewBox="0 0 44 44" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <circle cx="22" cy="22" r="20" fill="white" stroke="#111" stroke-width="3"/>
                      <text x="22" y="29" text-anchor="middle" fill="#111" font-family="Arial, sans-serif" font-size="16" font-weight="bold">1</text>
                    </svg>
                  `),
                  scaledSize: new window.google.maps.Size(44, 44),
                  anchor: new window.google.maps.Point(22, 22)
                });
              }
            });

            marker.addListener('mouseout', () => {
              if (!isSelected) {
                marker.setIcon({
                  url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
                    <svg width="30" height="30" viewBox="0 0 30 30" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <circle cx="15" cy="15" r="13" fill="#2563eb" stroke="#1e40af" stroke-width="2"/>
                      <text x="15" y="19" text-anchor="middle" fill="white" font-family="Arial, sans-serif" font-size="10" font-weight="bold">1</text>
                    </svg>
                  `),
                  scaledSize: new window.google.maps.Size(30, 30),
                  anchor: new window.google.maps.Point(15, 15)
                });
              }
            });

            markersRef.current.push(marker);
            console.log(`단일 마커 생성: 매물 ${property.id} (${property.location.lat}, ${property.location.lng})`);
          } else {
            const clusterMarker = createClusterMarker(cluster.properties, cluster.center);
            markersRef.current.push(clusterMarker);
            console.log(`클러스터 마커 생성: ${cluster.properties.length}개 매물 (중심: ${cluster.center.lat}, ${cluster.center.lng})`);
          }
        });
      }

      console.log(`총 ${markersRef.current.length}개의 마커 생성 완료`);
    };

    const updateMarkers = () => {
      if (!mapInstance.current) return;

      markersRef.current.forEach(marker => {
        if (marker && marker.setMap) {
          marker.setMap(null);
        }
      });
      markersRef.current = [];

      createMarkers();
    };

    const checkAndInit = () => {
      if (window.google && window.google.maps) {
        if (initMap()) {
          return;
        }
      }

      setTimeout(checkAndInit, 100);
    };

    checkAndInit();

    return () => {
      markersRef.current.forEach(marker => {
        if (marker && marker.setMap) {
          marker.setMap(null);
        }
      });
      markersRef.current = [];
    };
  }, [properties, onMarkerClick, onClusterClick, selectedMarkerId, setSelectedMarkerId, selectedClusterId, setSelectedClusterId]);

  return (
    <>
      <MapContainer ref={mapRef} />
    </>
  );
};

export default GoogleMap;
