const CoordinateResult = styled.div`
  margin-top: 0.5rem;
  padding: 0.5rem;
  background: #f0fdf4;
  border: 1px solid #10b981;
  border-radius: 4px;
  font-size: 0.75rem;
  color: #065f46;
