interface AddPropertyModalProps {
  onClose: () => void;
}

const AddPropertyModal: React.FC<AddPropertyModalProps> = ({ onClose }) => {
  const [isDragOver, setIsDragOver] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // 주소검색 관련 상태
  const [addressSearch, setAddressSearch] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [searchResult, setSearchResult] = useState<{ lat: number; lng: number } | null>(null);

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' || 
          file.type === 'application/vnd.ms-excel' ||
          file.name.endsWith('.xlsx') || 
          file.name.endsWith('.xls')) {
        setSelectedFile(file);
      } else {
        alert('엑셀 파일(.xlsx, .xls)만 업로드 가능합니다.');
      }
    }
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      const file = files[0];
      if (file.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' || 
          file.type === 'application/vnd.ms-excel' ||
          file.name.endsWith('.xlsx') || 
          file.name.endsWith('.xls')) {
        setSelectedFile(file);
      } else {
        alert('엑셀 파일(.xlsx, .xls)만 업로드 가능합니다.');
      }
    }
  };

  const handleUploadClick = () => {
    fileInputRef.current?.click();
  };

  const downloadTemplate = () => {
    // 엑셀 템플릿 다운로드 로직
    const templateData = [
      ['매물번호', '매물제목', '매물설명', '확인매물', '주소', '위도', '경도', '매물종류', '거래유형', '매매가', '보증금/임대료', '관리비', '융자금', '공급/전용면적', '건축물용도', '해당층/전체층', '방/화장실', '난방유형', '주차', '엘리베이터', '사용승인일', '입주가능일'],
      ['P001', '강남구 신축 아파트', '강남구 신축 아파트입니다', '확인매물', '서울시 강남구', '37.5665', '126.9780', '아파트', '매매', '5억원', '', '20만원', '', '50평/45평', '주거용', '5층/15층', '3방2욕', '개별난방', '가능', '있음', '2023-01-01', '즉시입주'],
      ['P002', '홍대입구 근처 원룸', '홍대입구 근처 원룸입니다', '확인매물', '서울시 마포구', '37.5575', '126.9250', '원룸', '임대', '', '보증금 1000만원/월세 50만원', '5만원', '', '20평/18평', '주거용', '3층/5층', '1방1욕', '개별난방', '불가능', '없음', '2020-05-01', '즉시입주']
    ];

    let csvContent = "data:text/csv;charset=utf-8,\uFEFF";
    templateData.forEach(row => {
      csvContent += row.join(',') + '\r\n';
    });

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement('a');
    link.setAttribute('href', encodedUri);
    link.setAttribute('download', '매물_등록_템플릿.csv');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('파일을 선택해주세요.');
      return;
    }

    setIsUploading(true);
    setUploadProgress(0);

    // 시뮬레이션된 업로드 진행률
    const interval = setInterval(() => {
      setUploadProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setIsUploading(false);
          alert('매물 등록이 완료되었습니다!');
          onClose();
          return 100;
        }
        return prev + 10;
      });
    }, 200);

    // 실제 구현에서는 여기에 파일 업로드 API 호출
    // const formData = new FormData();
    // formData.append('file', selectedFile);
    // const response = await fetch('/api/upload-properties', {
    //   method: 'POST',
    //   body: formData
    // });
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <ModalHeader>
          <ModalTitle>대량 매물 등록</ModalTitle>
          <CloseButton onClick={onClose}>&times;</CloseButton>
        </ModalHeader>

        <UploadSection>
          <FileUploadArea
            isDragOver={isDragOver}
            hasFile={!!selectedFile}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            onClick={handleUploadClick}
          >
            <UploadIcon>📁</UploadIcon>
            <UploadText>
              {selectedFile ? '파일이 선택되었습니다' : '엑셀 파일을 드래그하거나 클릭하여 업로드'}
            </UploadText>
            <UploadSubtext>
              {selectedFile ? '다른 파일로 변경하려면 클릭하세요' : '지원 형식: .xlsx, .xls'}
            </UploadSubtext>
            
            {selectedFile && (
              <FileInfo>
                <FileIcon>📄</FileIcon>
                <div>
                  <FileName>{selectedFile.name}</FileName>
                  <FileSize> ({formatFileSize(selectedFile.size)})</FileSize>
                </div>
              </FileInfo>
            )}
          </FileUploadArea>

          <FileInput
            ref={fileInputRef}
            type="file"
            accept=".xlsx,.xls"
            onChange={handleFileSelect}
          />

          <TemplateSection>
            <TemplateTitle>엑셀 템플릿 다운로드</TemplateTitle>
            <TemplateDescription>
              매물 정보를 입력할 수 있는 엑셀 템플릿을 다운로드하세요.
              템플릿에 맞춰 데이터를 입력한 후 업로드하시면 됩니다.
            </TemplateDescription>
            <TemplateButton onClick={downloadTemplate}>
              📥 템플릿 다운로드
            </TemplateButton>
          </TemplateSection>

          <ProgressSection className={isUploading ? 'visible' : ''}>
            <ProgressText>업로드 중... {uploadProgress}%</ProgressText>
          </ProgressSection>

          <ButtonGroup>
            <Button 
              onClick={handleUpload}
              disabled={!selectedFile || isUploading}
            >
              {isUploading ? '업로드 중...' : '매물 등록'}
            </Button>
            <CancelButton onClick={onClose}>
              취소
            </CancelButton>
          </ButtonGroup>
        </UploadSection>
      </ModalContent>
    </ModalOverlay>
  );
};

export default AddPropertyModal;
