import React from 'react';
import styled from 'styled-components';
import { Property } from '../types';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 8px;
  padding: 2rem;
  width: 90%;
  max-width: 800px;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #666;
  
  &:hover {
    color: #333;
  }
`;

const PropertyHeader = styled.div`
  margin-bottom: 2rem;
`;

const PropertyTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: bold;
  margin-bottom: 0.5rem;
  color: #1f2937;
`;

const PropertyPrice = styled.div`
  font-size: 1.25rem;
  font-weight: bold;
  color: #2563eb;
  margin-bottom: 1rem;
`;

const PropertyAddress = styled.div`
  font-size: 1rem;
  color: #666;
  margin-bottom: 1rem;
`;

const PropertyGrid = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 2rem;
  margin-bottom: 2rem;
`;

const PropertyImage = styled.div`
  height: 300px;
  background: #f3f4f6;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #9ca3af;
  font-size: 1rem;
`;

const PropertyInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const InfoSection = styled.div`
  padding: 1rem;
  border: 1px solid #e5e7eb;
  border-radius: 8px;
`;

const SectionTitle = styled.h3`
  font-size: 1rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: #374151;
`;

const InfoGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 0.5rem;
`;

const InfoItem = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0.5rem 0;
  border-bottom: 1px solid #f3f4f6;

  &:last-child {
    border-bottom: none;
  }
`;

const InfoLabel = styled.span`
  color: #666;
  font-size: 0.875rem;
`;

const InfoValue = styled.span`
  font-weight: 500;
  color: #1f2937;
`;

const PropertyFeatures = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 0.5rem;
`;

const FeatureTag = styled.span`
  background: #2563eb;
  color: white;
  padding: 0.25rem 0.75rem;
  border-radius: 20px;
  font-size: 0.875rem;
`;

const ContactSection = styled.div`
  background: #f9fafb;
  padding: 1.5rem;
  border-radius: 8px;
  margin-top: 1rem;
`;

const ContactTitle = styled.h3`
  font-size: 1rem;
  font-weight: 600;
  margin-bottom: 1rem;
  color: #374151;
`;

const ContactInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const ContactItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.875rem;
`;

const DescriptionSection = styled.div`
  margin-top: 1rem;
`;

const DescriptionText = styled.p`
  line-height: 1.6;
  color: #374151;
`;

interface PropertyDetailModalProps {
  property: Property;
  onClose: () => void;
}

const PropertyDetailModal: React.FC<PropertyDetailModalProps> = ({ property, onClose }) => {
  const formatPrice = (price: number) => {
    if (price >= 100000000) {
      return `${Math.floor(price / 100000000)}ÏñµÏõê`;
    } else if (price >= 10000) {
      return `${Math.floor(price / 10000)}ÎßåÏõê`;
    }
    return `${price.toLocaleString()}Ïõê`;
  };

  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        
        <PropertyHeader>
          <PropertyTitle>{property.title}</PropertyTitle>
          <PropertyPrice>{formatPrice(property.price)}</PropertyPrice>
          <PropertyAddress>{property.address}</PropertyAddress>
        </PropertyHeader>

        <PropertyGrid>
          <PropertyImage>
            Îß§Î¨º Ïù¥ÎØ∏ÏßÄ
          </PropertyImage>
          
          <PropertyInfo>
            <InfoSection>
              <SectionTitle>Í∏∞Î≥∏ Ï†ïÎ≥¥</SectionTitle>
              <InfoGrid>
                <InfoItem>
                  <InfoLabel>Í±∞Îûò Ïú†Ìòï</InfoLabel>
                  <InfoValue>{property.type === 'sale' ? 'Îß§Îß§' : 'ÏûÑÎåÄ'}</InfoValue>
                </InfoItem>
                <InfoItem>
                  <InfoLabel>Îß§Î¨º Ïú†Ìòï</InfoLabel>
                  <InfoValue>
                    {property.propertyType === 'apartment' ? 'ÏïÑÌååÌä∏' :
                     property.propertyType === 'house' ? 'Îã®ÎèÖÏ£ºÌÉù' :
                     property.propertyType === 'commercial' ? 'ÏÉÅÍ∞Ä' : 'ÌÜ†ÏßÄ'}
                  </InfoValue>
                </InfoItem>
                <InfoItem>
                  <InfoLabel>Î©¥Ï†Å</InfoLabel>
                  <InfoValue>{property.area}„é°</InfoValue>
                </InfoItem>
                {property.bedrooms && (
                  <InfoItem>
                    <InfoLabel>Ïπ®Ïã§</InfoLabel>
                    <InfoValue>{property.bedrooms}Í∞ú</InfoValue>
                  </InfoItem>
                )}
                {property.bathrooms && (
                  <InfoItem>
                    <InfoLabel>ÏöïÏã§</InfoLabel>
                    <InfoValue>{property.bathrooms}Í∞ú</InfoValue>
                  </InfoItem>
                )}
              </InfoGrid>
            </InfoSection>

            <InfoSection>
              <SectionTitle>ÌäπÏßï</SectionTitle>
              <PropertyFeatures>
                {property.features.map((feature, index) => (
                  <FeatureTag key={index}>{feature}</FeatureTag>
                ))}
              </PropertyFeatures>
            </InfoSection>

            <ContactSection>
              <ContactTitle>Ïó∞ÎùΩÏ≤ò</ContactTitle>
              <ContactInfo>
                <ContactItem>
                  <span>üè¢</span>
                  <span>{property.contact.name}</span>
                </ContactItem>
                <ContactItem>
                  <span>üìû</span>
                  <span>{property.contact.phone}</span>
                </ContactItem>
                <ContactItem>
                  <span>‚úâÔ∏è</span>
                  <span>{property.contact.email}</span>
                </ContactItem>
              </ContactInfo>
            </ContactSection>
          </PropertyInfo>
        </PropertyGrid>

        <DescriptionSection>
          <SectionTitle>Îß§Î¨º ÏÑ§Î™Ö</SectionTitle>
          <DescriptionText>
            {property.description}
          </DescriptionText>
        </DescriptionSection>
      </ModalContent>
    </ModalOverlay>
  );
};

export default PropertyDetailModal;
