import React from 'react';
import styled from 'styled-components';
import { Property } from '../types';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 8px;
  padding: 2rem;
  width: 90%;
  max-width: 600px;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #666;
  
  &:hover {
    color: #333;
  }
`;

const PropertyHeader = styled.div`
  margin-bottom: 1.5rem;
`;

const PropertyTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: bold;
  color: #1f2937;
  margin-bottom: 0.5rem;
`;

const PropertyPrice = styled.div`
  font-size: 1.25rem;
  font-weight: bold;
  color: #2563eb;
  margin-bottom: 0.5rem;
`;

const PropertyAddress = styled.div`
  font-size: 1rem;
  color: #666;
  margin-bottom: 1rem;
`;

const PropertyGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
  margin-bottom: 1.5rem;
`;

const PropertyImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-radius: 8px;
  margin-bottom: 1rem;
`;

const PropertyInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const InfoSection = styled.div`
  margin-bottom: 1.5rem;
`;

const SectionTitle = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.75rem;
`;

const InfoGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
`;

const InfoItem = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
`;

const InfoLabel = styled.span`
  font-size: 0.875rem;
  color: #6b7280;
  font-weight: 500;
`;

const InfoValue = styled.span`
  font-size: 1rem;
  color: #1f2937;
  font-weight: 500;
`;

const PropertyFeatures = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 0.5rem;
`;

const FeatureTag = styled.span`
  background: #e5e7eb;
  color: #374151;
  padding: 0.25rem 0.75rem;
  border-radius: 20px;
  font-size: 0.875rem;
  font-weight: 500;
`;

const ContactSection = styled.div`
  margin-top: 1.5rem;
  padding-top: 1.5rem;
  border-top: 1px solid #e5e7eb;
`;

const ContactTitle = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.75rem;
`;

const ContactInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const ContactItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 1rem;
  color: #1f2937;
`;

const DescriptionSection = styled.div`
  margin-top: 1.5rem;
`;

const DescriptionText = styled.p`
  font-size: 1rem;
  line-height: 1.6;
  color: #374151;
`;

interface PropertyDetailModalProps {
  property: Property;
  onClose: () => void;
}

const formatPrice = (price: number) => {
  if (price >= 100000000) {
    return `${Math.floor(price / 100000000)}ÏñµÏõê`;
  } else if (price >= 10000) {
    return `${Math.floor(price / 10000)}ÎßåÏõê`;
  }
  return `${price.toLocaleString()}Ïõê`;
};

const PropertyDetailModal: React.FC<PropertyDetailModalProps> = ({ property, onClose }) => {
  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        
        <PropertyHeader>
          <PropertyTitle>{property.title}</PropertyTitle>
          <PropertyPrice>{formatPrice(property.price)}</PropertyPrice>
          <PropertyAddress>{property.address}</PropertyAddress>
        </PropertyHeader>

        {property.images && property.images.length > 0 && (
          <PropertyImage src={property.images[0]} alt={property.title} />
        )}

        <PropertyInfo>
          <InfoSection>
            <SectionTitle>Í∏∞Î≥∏ Ï†ïÎ≥¥</SectionTitle>
            <InfoGrid>
              <InfoItem>
                <InfoLabel>Í±∞Îûò Ïú†Ìòï</InfoLabel>
                <InfoValue>{property.type === 'sale' ? 'Îß§Îß§' : 'ÏûÑÎåÄ'}</InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>Îß§Î¨º Ïú†Ìòï</InfoLabel>
                <InfoValue>
                  {property.propertyType === 'apartment' ? 'ÏïÑÌååÌä∏' :
                   property.propertyType === 'house' ? 'Îã®ÎèÖÏ£ºÌÉù' :
                   property.propertyType === 'commercial' ? 'ÏÉÅÍ∞Ä' : 'ÌÜ†ÏßÄ'}
                </InfoValue>
              </InfoItem>
              {property.bedrooms && (
                <InfoItem>
                  <InfoLabel>Ïπ®Ïã§</InfoLabel>
                  <InfoValue>{property.bedrooms}Í∞ú</InfoValue>
                </InfoItem>
              )}
              {property.bathrooms && (
                <InfoItem>
                  <InfoLabel>ÏöïÏã§</InfoLabel>
                  <InfoValue>{property.bathrooms}Í∞ú</InfoValue>
                </InfoItem>
              )}
              {property.area && (
                <InfoItem>
                  <InfoLabel>Î©¥Ï†Å</InfoLabel>
                  <InfoValue>{property.area}„é°</InfoValue>
                </InfoItem>
              )}
            </InfoGrid>
          </InfoSection>

          {property.features && property.features.length > 0 && (
            <InfoSection>
              <SectionTitle>ÌäπÏßï</SectionTitle>
              <PropertyFeatures>
                {property.features.map((feature, index) => (
                  <FeatureTag key={index}>{feature}</FeatureTag>
                ))}
              </PropertyFeatures>
            </InfoSection>
          )}

          <ContactSection>
            <ContactTitle>Ïó∞ÎùΩÏ≤ò</ContactTitle>
            <ContactInfo>
              <ContactItem>
                <span>üìû</span>
                <span>{property.contact.name}</span>
              </ContactItem>
              <ContactItem>
                <span>üì±</span>
                <span>{property.contact.phone}</span>
              </ContactItem>
              <ContactItem>
                <span>‚úâÔ∏è</span>
                <span>{property.contact.email}</span>
              </ContactItem>
            </ContactInfo>
          </ContactSection>

          {property.description && (
            <DescriptionSection>
              <SectionTitle>ÏÉÅÏÑ∏ ÏÑ§Î™Ö</SectionTitle>
              <DescriptionText>{property.description}</DescriptionText>
            </DescriptionSection>
          )}
        </PropertyInfo>
      </ModalContent>
    </ModalOverlay>
  );
};

export default PropertyDetailModal;
