import React, { useState } from 'react';
import styled from 'styled-components';
import GoogleMap from '../components/GoogleMap';
import PropertyDetailModal from '../components/PropertyDetailModal';
import { Property } from '../types';

const HomeContainer = styled.div`
  display: flex;
  height: calc(100vh - 120px);
  min-height: 500px;
  overflow: hidden;
`;

const MapSection = styled.div`
  flex: 1;
  position: relative;
  min-width: 0;
`;

const Sidebar = styled.div`
  width: 400px;
  background: white;
  border-left: 1px solid #e5e7eb;
  overflow-y: auto;
  padding: 1rem;
  flex-shrink: 0;
`;

const SearchSection = styled.div`
  padding: 1rem;
  border-bottom: 1px solid #e5e7eb;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 1rem;

  &:focus {
    outline: none;
    border-color: #2563eb;
    box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
  }
`;

const FilterSection = styled.div`
  padding: 1rem;
  border-bottom: 1px solid #e5e7eb;
`;

const FilterTitle = styled.h3`
  margin-bottom: 0.5rem;
  font-size: 1rem;
  font-weight: 600;
`;

const FilterGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 0.5rem;
`;

const FilterSelect = styled.select`
  padding: 0.5rem;
  border: 1px solid #d1d5db;
  border-radius: 4px;
  font-size: 0.875rem;
`;

const PropertyCard = styled.div`
  padding: 1rem;
  border: 1px solid #e5e7eb;
  border-radius: 8px;
  margin-bottom: 1rem;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    border-color: #2563eb;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  }
`;

const PropertyTitle = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const PropertyPrice = styled.div`
  font-size: 1.25rem;
  font-weight: bold;
  color: #2563eb;
  margin-bottom: 0.5rem;
`;

const PropertyDetails = styled.div`
  font-size: 0.875rem;
  color: #666;
  margin-bottom: 0.5rem;
`;

const PropertyAddress = styled.div`
  font-size: 0.875rem;
  color: #999;
`;

// 샘플 데이터
const sampleProperties: Property[] = [
  {
    id: '1',
    title: '강남구 신축 아파트',
    description: '강남구 역삼동 신축 아파트입니다. 역세권으로 교통이 편리합니다.',
    price: 850000000,
    type: 'sale',
    propertyType: 'apartment',
    address: '서울시 강남구 역삼동 123-45',
    location: { lat: 37.5013, lng: 127.0396 },
    bedrooms: 3,
    bathrooms: 2,
    area: 84.5,
    images: ['https://via.placeholder.com/300x200'],
    contact: {
      name: '김부동산',
      phone: '02-1234-5678',
      email: 'kim@realestate.com'
    },
    features: ['역세권', '신축', '주차가능'],
    createdAt: new Date(),
    isActive: true
  },
  {
    id: '2',
    title: '홍대입구 근처 원룸',
    description: '홍대입구역 도보 5분 거리의 원룸입니다.',
    price: 500000,
    type: 'rent',
    propertyType: 'apartment',
    address: '서울시 마포구 서교동 456-78',
    location: { lat: 37.5572, lng: 126.9254 },
    bedrooms: 1,
    bathrooms: 1,
    area: 25.3,
    images: ['https://via.placeholder.com/300x200'],
    contact: {
      name: '박부동산',
      phone: '02-9876-5432',
      email: 'park@realestate.com'
    },
    features: ['역세권', '원룸', '주차불가'],
    createdAt: new Date(),
    isActive: true
  }
];

const HomePage: React.FC = () => {
  const [properties] = useState<Property[]>(sampleProperties);
  const [selectedProperty, setSelectedProperty] = useState<Property | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    type: '',
    propertyType: '',
    minPrice: '',
    maxPrice: ''
  });

  const handleMarkerClick = (property: Property) => {
    setSelectedProperty(property);
  };

  const handlePropertyCardClick = (property: Property) => {
    setSelectedProperty(property);
  };

  const formatPrice = (price: number) => {
    if (price >= 100000000) {
      return `${Math.floor(price / 100000000)}억원`;
    } else if (price >= 10000) {
      return `${Math.floor(price / 10000)}만원`;
    }
    return `${price.toLocaleString()}원`;
  };

  const filteredProperties = properties.filter(property => {
    const matchesSearch = property.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         property.address.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesType = !filters.type || property.type === filters.type;
    const matchesPropertyType = !filters.propertyType || property.propertyType === filters.propertyType;
    const matchesMinPrice = !filters.minPrice || property.price >= parseInt(filters.minPrice);
    const matchesMaxPrice = !filters.maxPrice || property.price <= parseInt(filters.maxPrice);

    return matchesSearch && matchesType && matchesPropertyType && matchesMinPrice && matchesMaxPrice;
  });

  return (
    <>
      <HomeContainer>
        <MapSection>
          <GoogleMap
            properties={filteredProperties}
            onMarkerClick={handleMarkerClick}
          />
        </MapSection>
        <Sidebar>
          <SearchSection>
            <SearchInput
              type="text"
              placeholder="지역명, 역명, 매물명으로 검색..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </SearchSection>
          <FilterSection>
            <FilterTitle>필터</FilterTitle>
            <FilterGrid>
              <FilterSelect
                value={filters.type}
                onChange={(e) => setFilters({ ...filters, type: e.target.value })}
              >
                <option value="">거래 유형</option>
                <option value="sale">매매</option>
                <option value="rent">임대</option>
              </FilterSelect>
              <FilterSelect
                value={filters.propertyType}
                onChange={(e) => setFilters({ ...filters, propertyType: e.target.value })}
              >
                <option value="">매물 유형</option>
                <option value="apartment">아파트</option>
                <option value="house">단독주택</option>
                <option value="commercial">상가</option>
                <option value="land">토지</option>
              </FilterSelect>
            </FilterGrid>
          </FilterSection>
          <div>
            {filteredProperties.map(property => (
              <PropertyCard
                key={property.id}
                onClick={() => handlePropertyCardClick(property)}
              >
                <PropertyTitle>{property.title}</PropertyTitle>
                <PropertyPrice>{formatPrice(property.price)}</PropertyPrice>
                <PropertyDetails>
                  {property.bedrooms && `${property.bedrooms}침실`}
                  {property.bathrooms && ` ${property.bathrooms}욕실`}
                  {property.area && ` ${property.area}㎡`}
                </PropertyDetails>
                <PropertyAddress>{property.address}</PropertyAddress>
              </PropertyCard>
            ))}
          </div>
        </Sidebar>
      </HomeContainer>
      
      {selectedProperty && (
        <PropertyDetailModal
          property={selectedProperty}
          onClose={() => setSelectedProperty(null)}
        />
      )}
    </>
  );
};

export default HomePage;
