import React, { useState } from 'react';
import styled from 'styled-components';
import GoogleMap from '../components/GoogleMap';
import { Property } from '../types';

const HomeContainer = styled.div`
  display: flex;
  height: calc(100vh - 120px);
  min-height: 500px;
  overflow: hidden;
`;

const MapSection = styled.div`
  flex: 1;
  position: relative;
  min-width: 0;
`;

const Sidebar = styled.div<{ isOpen: boolean }>`
  width: ${props => props.isOpen ? '400px' : '0px'};
  background: white;
  border-left: 1px solid #e5e7eb;
  overflow-y: auto;
  padding: ${props => props.isOpen ? '1rem' : '0'};
  flex-shrink: 0;
  transition: all 0.3s ease;
  position: relative;
`;

const ToggleButton = styled.button<{ isOpen: boolean }>`
  position: absolute;
  left: ${props => props.isOpen ? '-40px' : '-40px'};
  top: 50%;
  transform: translateY(-50%);
  width: 40px;
  height: 60px;
  background: #2563eb;
  color: white;
  border: none;
  border-radius: 8px 0 0 8px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.2rem;
  font-weight: bold;
  transition: all 0.3s ease;
  z-index: 100;

  &:hover {
    background: #1d4ed8;
  }
`;

const PropertyCard = styled.div`
  padding: 1rem;
  border: 1px solid #e5e7eb;
  border-radius: 8px;
  margin-bottom: 1rem;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    border-color: #2563eb;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  }
`;

const PropertyTitle = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const PropertyPrice = styled.div`
  font-size: 1.25rem;
  font-weight: bold;
  color: #2563eb;
  margin-bottom: 0.5rem;
`;

const PropertyDetails = styled.div`
  font-size: 0.875rem;
  color: #666;
  margin-bottom: 0.5rem;
`;

const PropertyAddress = styled.div`
  font-size: 0.875rem;
  color: #999;
`;

const SidebarTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: bold;
  color: #1f2937;
  margin-bottom: 1rem;
  text-align: center;
`;

const PropertyDetailModal = styled.div<{ isOpen: boolean }>`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: ${props => props.isOpen ? 'flex' : 'none'};
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 8px;
  padding: 2rem;
  width: 90%;
  max-width: 600px;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #666;
  
  &:hover {
    color: #333;
  }
`;

const ModalTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: bold;
  color: #1f2937;
  margin-bottom: 1rem;
`;

const ModalPrice = styled.div`
  font-size: 1.25rem;
  font-weight: bold;
  color: #2563eb;
  margin-bottom: 1rem;
`;

const ModalAddress = styled.div`
  font-size: 1rem;
  color: #666;
  margin-bottom: 1rem;
`;

const ModalDescription = styled.p`
  font-size: 1rem;
  line-height: 1.6;
  color: #374151;
  margin-bottom: 1rem;
`;

const ModalInfo = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
  margin-bottom: 1rem;
`;

const InfoItem = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
`;

const InfoLabel = styled.span`
  font-size: 0.875rem;
  color: #6b7280;
  font-weight: 500;
`;

const InfoValue = styled.span`
  font-size: 1rem;
  color: #1f2937;
  font-weight: 500;
`;

const ContactInfo = styled.div`
  margin-top: 1.5rem;
  padding-top: 1.5rem;
  border-top: 1px solid #e5e7eb;
`;

const ContactTitle = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.75rem;
`;

const ContactItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 1rem;
  color: #1f2937;
  margin-bottom: 0.5rem;
`;

// 샘플 데이터
const sampleProperties: Property[] = [
  {
    id: '1',
    title: '강남구 신축 아파트',
    description: '강남구 역삼동 신축 아파트입니다. 역세권으로 교통이 편리합니다.',
    price: 850000000,
    type: 'sale',
    propertyType: 'apartment',
    address: '서울시 강남구 역삼동 123-45',
    location: { lat: 37.5013, lng: 127.0396 },
    bedrooms: 3,
    bathrooms: 2,
    area: 84.5,
    images: ['https://via.placeholder.com/300x200'],
    contact: {
      name: '김부동산',
      phone: '02-1234-5678',
      email: 'kim@realestate.com'
    },
    features: ['역세권', '신축', '주차가능'],
    createdAt: new Date(),
    isActive: true
  },
  {
    id: '2',
    title: '홍대입구 근처 원룸',
    description: '홍대입구역 도보 5분 거리의 원룸입니다.',
    price: 500000,
    type: 'rent',
    propertyType: 'apartment',
    address: '서울시 마포구 서교동 456-78',
    location: { lat: 37.5572, lng: 126.9254 },
    bedrooms: 1,
    bathrooms: 1,
    area: 25.3,
    images: ['https://via.placeholder.com/300x200'],
    contact: {
      name: '박부동산',
      phone: '02-9876-5432',
      email: 'park@realestate.com'
    },
    features: ['역세권', '원룸', '주차불가'],
    createdAt: new Date(),
    isActive: true
  }
];

const HomePage: React.FC = () => {
  const [properties] = useState<Property[]>(sampleProperties);
  const [selectedProperty, setSelectedProperty] = useState<Property | null>(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [filteredProperties, setFilteredProperties] = useState<Property[]>(sampleProperties);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalProperty, setModalProperty] = useState<Property | null>(null);

  const handleMarkerClick = (property: Property) => {
    setSelectedProperty(property);
    setFilteredProperties([property]); // 해당 매물만 보이도록 필터링
    setIsSidebarOpen(true); // 사이드바 열기
  };

  const handlePropertyCardClick = (property: Property) => {
    setModalProperty(property);
    setIsModalOpen(true);
  };

  const handleShowAllProperties = () => {
    setSelectedProperty(null);
    setFilteredProperties(properties); // 모든 매물 보이기
  };

  const formatPrice = (price: number) => {
    if (price >= 100000000) {
      return `${Math.floor(price / 100000000)}억원`;
    } else if (price >= 10000) {
      return `${Math.floor(price / 10000)}만원`;
    }
    return `${price.toLocaleString()}원`;
  };

  return (
    <>
      <HomeContainer>
        <MapSection>
          <GoogleMap
            properties={properties}
            onMarkerClick={handleMarkerClick}
          />
        </MapSection>
        <Sidebar isOpen={isSidebarOpen}>
          <ToggleButton 
            isOpen={isSidebarOpen}
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          >
            {isSidebarOpen ? '◀' : '▶'}
          </ToggleButton>
          {isSidebarOpen && (
            <>
              <SidebarTitle>
                {selectedProperty ? '선택된 매물' : '매물 목록'}
                {selectedProperty && (
                  <button 
                    onClick={handleShowAllProperties}
                    style={{
                      marginLeft: '1rem',
                      padding: '0.25rem 0.5rem',
                      fontSize: '0.75rem',
                      background: '#2563eb',
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                  >
                    전체보기
                  </button>
                )}
              </SidebarTitle>
              {filteredProperties.map(property => (
                <PropertyCard
                  key={property.id}
                  onClick={() => handlePropertyCardClick(property)}
                >
                  <PropertyTitle>{property.title}</PropertyTitle>
                  <PropertyPrice>{formatPrice(property.price)}</PropertyPrice>
                  <PropertyDetails>
                    {property.bedrooms && `${property.bedrooms}침실`}
                    {property.bathrooms && ` ${property.bathrooms}욕실`}
                    {property.area && ` ${property.area}㎡`}
                  </PropertyDetails>
                  <PropertyAddress>{property.address}</PropertyAddress>
                </PropertyCard>
              ))}
            </>
          )}
        </Sidebar>
      </HomeContainer>
      
      <PropertyDetailModal isOpen={isModalOpen} onClick={() => setIsModalOpen(false)}>
        <ModalContent onClick={(e) => e.stopPropagation()}>
          <CloseButton onClick={() => setIsModalOpen(false)}>&times;</CloseButton>
          {modalProperty && (
            <>
              <ModalTitle>{modalProperty.title}</ModalTitle>
              <ModalPrice>{formatPrice(modalProperty.price)}</ModalPrice>
              <ModalAddress>{modalProperty.address}</ModalAddress>
              <ModalDescription>{modalProperty.description}</ModalDescription>
              
              <ModalInfo>
                <InfoItem>
                  <InfoLabel>거래 유형</InfoLabel>
                  <InfoValue>{modalProperty.type === 'sale' ? '매매' : '임대'}</InfoValue>
                </InfoItem>
                <InfoItem>
                  <InfoLabel>매물 유형</InfoLabel>
                  <InfoValue>
                    {modalProperty.propertyType === 'apartment' ? '아파트' :
                     modalProperty.propertyType === 'house' ? '단독주택' :
                     modalProperty.propertyType === 'commercial' ? '상가' : '토지'}
                  </InfoValue>
                </InfoItem>
                {modalProperty.bedrooms && (
                  <InfoItem>
                    <InfoLabel>침실</InfoLabel>
                    <InfoValue>{modalProperty.bedrooms}개</InfoValue>
                  </InfoItem>
                )}
                {modalProperty.bathrooms && (
                  <InfoItem>
                    <InfoLabel>욕실</InfoLabel>
                    <InfoValue>{modalProperty.bathrooms}개</InfoValue>
                  </InfoItem>
                )}
                {modalProperty.area && (
                  <InfoItem>
                    <InfoLabel>면적</InfoLabel>
                    <InfoValue>{modalProperty.area}㎡</InfoValue>
                  </InfoItem>
                )}
              </ModalInfo>

              <ContactInfo>
                <ContactTitle>연락처</ContactTitle>
                <ContactItem>
                  <span>📞</span>
                  <span>{modalProperty.contact.name}</span>
                </ContactItem>
                <ContactItem>
                  <span>📱</span>
                  <span>{modalProperty.contact.phone}</span>
                </ContactItem>
                <ContactItem>
                  <span>✉️</span>
                  <span>{modalProperty.contact.email}</span>
                </ContactItem>
              </ContactInfo>
            </>
          )}
        </ModalContent>
      </PropertyDetailModal>
    </>
  );
};

export default HomePage;
