import React, { useState } from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 8px;
  padding: 2rem;
  width: 90%;
  max-width: 600px;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #666;
  
  &:hover {
    color: #333;
  }
`;

const Title = styled.h2`
  font-size: 1.5rem;
  font-weight: bold;
  margin-bottom: 1.5rem;
  color: #1f2937;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const FormSection = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const Label = styled.label`
  font-weight: 600;
  color: #374151;
  font-size: 0.875rem;
`;

const Input = styled.input`
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 1rem;

  &:focus {
    outline: none;
    border-color: #2563eb;
    box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
  }
`;

const Select = styled.select`
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 1rem;

  &:focus {
    outline: none;
    border-color: #2563eb;
    box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
  }
`;

const Textarea = styled.textarea`
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 1rem;
  resize: vertical;
  min-height: 100px;

  &:focus {
    outline: none;
    border-color: #2563eb;
    box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
  }
`;

const Button = styled.button`
  background: #2563eb;
  color: white;
  padding: 1rem 2rem;
  border-radius: 6px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background: #1d4ed8;
  }

  &:disabled {
    background: #9ca3af;
    cursor: not-allowed;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const CancelButton = styled.button`
  background: #6b7280;
  color: white;
  padding: 1rem 2rem;
  border-radius: 6px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background: #4b5563;
  }
`;

interface AddPropertyModalProps {
  onClose: () => void;
}

const AddPropertyModal: React.FC<AddPropertyModalProps> = ({ onClose }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    type: 'sale',
    propertyType: 'apartment',
    address: '',
    lat: '',
    lng: '',
    bedrooms: '',
    bathrooms: '',
    area: '',
    contactName: '',
    contactPhone: '',
    contactEmail: '',
    features: ''
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    // 여기서 실제로는 API 호출을 통해 매물을 등록합니다
    console.log('매물 등록:', formData);
    
    // 성공 후 모달 닫기
    onClose();
  };

  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <Title>매물 등록</Title>
        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label>매물 제목 *</Label>
            <Input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleInputChange}
              required
              placeholder="매물 제목을 입력하세요"
            />
          </FormGroup>

          <FormGroup>
            <Label>매물 설명</Label>
            <Textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              placeholder="매물에 대한 상세한 설명을 입력하세요"
            />
          </FormGroup>

          <FormSection>
            <FormGroup>
              <Label>가격 *</Label>
              <Input
                type="number"
                name="price"
                value={formData.price}
                onChange={handleInputChange}
                required
                placeholder="가격을 입력하세요"
              />
            </FormGroup>

            <FormGroup>
              <Label>거래 유형 *</Label>
              <Select
                name="type"
                value={formData.type}
                onChange={handleInputChange}
                required
              >
                <option value="sale">매매</option>
                <option value="rent">임대</option>
              </Select>
            </FormGroup>
          </FormSection>

          <FormSection>
            <FormGroup>
              <Label>매물 유형 *</Label>
              <Select
                name="propertyType"
                value={formData.propertyType}
                onChange={handleInputChange}
                required
              >
                <option value="apartment">아파트</option>
                <option value="house">단독주택</option>
                <option value="commercial">상가</option>
                <option value="land">토지</option>
              </Select>
            </FormGroup>

            <FormGroup>
              <Label>면적 (㎡) *</Label>
              <Input
                type="number"
                name="area"
                value={formData.area}
                onChange={handleInputChange}
                required
                placeholder="면적을 입력하세요"
              />
            </FormGroup>
          </FormSection>

          <FormSection>
            <FormGroup>
              <Label>침실 수</Label>
              <Input
                type="number"
                name="bedrooms"
                value={formData.bedrooms}
                onChange={handleInputChange}
                placeholder="침실 수"
              />
            </FormGroup>

            <FormGroup>
              <Label>욕실 수</Label>
              <Input
                type="number"
                name="bathrooms"
                value={formData.bathrooms}
                onChange={handleInputChange}
                placeholder="욕실 수"
              />
            </FormGroup>
          </FormSection>

          <FormGroup>
            <Label>주소 *</Label>
            <Input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleInputChange}
              required
              placeholder="주소를 입력하세요"
            />
          </FormGroup>

          <FormSection>
            <FormGroup>
              <Label>위도</Label>
              <Input
                type="number"
                name="lat"
                value={formData.lat}
                onChange={handleInputChange}
                placeholder="위도"
                step="any"
              />
            </FormGroup>

            <FormGroup>
              <Label>경도</Label>
              <Input
                type="number"
                name="lng"
                value={formData.lng}
                onChange={handleInputChange}
                placeholder="경도"
                step="any"
              />
            </FormGroup>
          </FormSection>

          <FormGroup>
            <Label>특징 (쉼표로 구분)</Label>
            <Input
              type="text"
              name="features"
              value={formData.features}
              onChange={handleInputChange}
              placeholder="역세권, 신축, 주차가능 등"
            />
          </FormGroup>

          <FormSection>
            <FormGroup>
              <Label>연락처 이름 *</Label>
              <Input
                type="text"
                name="contactName"
                value={formData.contactName}
                onChange={handleInputChange}
                required
                placeholder="연락처 이름"
              />
            </FormGroup>

            <FormGroup>
              <Label>연락처 전화번호 *</Label>
              <Input
                type="tel"
                name="contactPhone"
                value={formData.contactPhone}
                onChange={handleInputChange}
                required
                placeholder="전화번호"
              />
            </FormGroup>
          </FormSection>

          <FormGroup>
            <Label>연락처 이메일 *</Label>
            <Input
              type="email"
              name="contactEmail"
              value={formData.contactEmail}
              onChange={handleInputChange}
              required
              placeholder="이메일 주소"
            />
          </FormGroup>

          <ButtonGroup>
            <Button type="submit">매물 등록</Button>
            <CancelButton type="button" onClick={onClose}>
              취소
            </CancelButton>
          </ButtonGroup>
        </Form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default AddPropertyModal;
