const HomePage = ({
  searchTerm = '',
  addressSearch = '',
  filters = {
    type: '',
    propertyType: '',
    area: '',
    price: '',
    deposit: ''
  },
  onPropertyAdded
}) => {
  _s();
  const [selectedProperty, setSelectedProperty] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalProperty, setModalProperty] = useState(null);
  const [properties, setProperties] = useState(sampleProperties);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [selectedClusterProperties, setSelectedClusterProperties] = useState([]);
  const [selectedMarkerId, setSelectedMarkerId] = useState(null);

  // 매물 수정 관련 상태
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [editingProperty, setEditingProperty] = useState(null);
  const [editForm, setEditForm] = useState({
    title: '',
    description: '',
    price: '',
    deposit: '',
    address: '',
    lat: '',
    lng: '',
    bedrooms: '',
    bathrooms: '',
    area: '',
    type: '',
    propertyType: '',
    contactName: '',
    contactPhone: '',
    contactEmail: '',
    confirmedDate: ''
  });

  // 사진 슬라이드 관련 상태
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [mainImageIndex, setMainImageIndex] = useState(0);

  // 사진 업로드 관련 상태
  const [uploadedImages, setUploadedImages] = useState([]);
  const fileInputRef = useRef(null);

  // 매물 추가 처리
  const handlePropertyAdded = newProperties => {
    setProperties(prev => [...prev, ...newProperties]);
    if (onPropertyAdded) {
      onPropertyAdded(newProperties);
    }
  };

  // 매물 수정 시작
  const handleEditProperty = property => {
    setEditingProperty(property);
    setEditForm({
      title: property.title,
      description: property.description,
      price: property.price.toString(),
      deposit: (property.deposit || 0).toString(),
      address: property.address,
      lat: property.location.lat.toString(),
      lng: property.location.lng.toString(),
      bedrooms: (property.bedrooms || 0).toString(),
      bathrooms: (property.bathrooms || 0).toString(),
      area: property.area.toString(),
      type: property.type,
      propertyType: property.propertyType,
      contactName: property.contact.name,
      contactPhone: property.contact.phone,
      contactEmail: property.contact.email,
      confirmedDate: property.confirmedDate || '25.07.19'
    });
    setIsEditModalOpen(true);
  };

  // 매물 수정 저장
  const handleSaveProperty = e => {
    e.preventDefault();
    if (!editingProperty) return;
    const updatedProperty = {
      ...editingProperty,
      title: editForm.title,
      description: editForm.description,
      price: parseInt(editForm.price) || 0,
      deposit: parseInt(editForm.deposit) || 0,
      address: editForm.address,
      location: {
        lat: parseFloat(editForm.lat) || 0,
        lng: parseFloat(editForm.lng) || 0
      },
      bedrooms: parseInt(editForm.bedrooms) || 0,
      bathrooms: parseInt(editForm.bathrooms) || 0,
      area: parseFloat(editForm.area) || 0,
      type: editForm.type,
      propertyType: editForm.propertyType,
      contact: {
        name: editForm.contactName,
        phone: editForm.contactPhone,
        email: editForm.contactEmail
      },
      confirmedDate: editForm.confirmedDate
    };
    setProperties(prev => prev.map(p => p.id === editingProperty.id ? updatedProperty : p));
    setIsEditModalOpen(false);
    setEditingProperty(null);
    alert('매물이 성공적으로 수정되었습니다!');
  };

  // 매물 수정 취소
  const handleCancelEdit = () => {
    setIsEditModalOpen(false);
    setEditingProperty(null);
  };

  // 사진 슬라이드 관련 함수들
  const handleImageChange = index => {
    setCurrentImageIndex(index);
  };
  const handlePrevImage = () => {
    if (uploadedImages.length > 0) {
      setCurrentImageIndex(prev => prev === 0 ? uploadedImages.length - 1 : prev - 1);
    }
  };
  const handleNextImage = () => {
    if (uploadedImages.length > 0) {
      setCurrentImageIndex(prev => prev === uploadedImages.length - 1 ? 0 : prev + 1);
    }
  };
  const handleSetMainImage = index => {
    setMainImageIndex(index);
    // 실제로는 매물의 대표사진을 업데이트하는 로직이 필요
    alert(`대표사진이 ${index + 1}번째 사진으로 설정되었습니다.`);
  };

  // 매물 상세보기 열 때 사진 인덱스 초기화
  const handlePropertyCardClick = property => {
    setModalProperty(property);
    setIsModalOpen(true);
    setCurrentImageIndex(0);
    setMainImageIndex(0);
    // 업로드된 이미지 초기화
    setUploadedImages([]);
  };
  const handleMarkerClick = property => {
    // 해당 위치의 모든 매물을 찾기
    const nearbyProperties = properties.filter(p => Math.abs(p.location.lat - property.location.lat) < 0.001 && Math.abs(p.location.lng - property.location.lng) < 0.001);
    console.log(`마커 클릭: ${property.title} 위치의 매물 ${nearbyProperties.length}개 발견`);
    setSelectedProperty(property);
    // 지도 상태 변경 제거 - 사용자가 원하는 위치에서 계속 탐색할 수 있도록
    // setSelectedLocation(property.location);
    setSelectedClusterProperties([]); // 클러스터 선택 해제
    setSelectedMarkerId(property.id); // 마커 선택
    setIsSidebarOpen(true); // 사이드바 열기
  };
  const handleClusterClick = clusterProperties => {
    console.log(`클러스터 클릭: ${clusterProperties.length}개 매물 선택`);
    setSelectedClusterProperties(clusterProperties);
    setSelectedProperty(null);
    // 지도 상태 변경 제거 - 사용자가 원하는 위치에서 계속 탐색할 수 있도록
    // setSelectedLocation(null);
    setIsSidebarOpen(true); // 사이드바 열기
  };
  const handleShowAllProperties = () => {
    setSelectedProperty(null);
    setSelectedLocation(null);
    setSelectedClusterProperties([]);
  };
  const formatPrice = price => {
    if (price >= 100000000) {
      return `${Math.floor(price / 100000000)}억원`;
    } else if (price >= 10000) {
      return `${Math.floor(price / 10000)}만원`;
    }
    return `${price.toLocaleString()}원`;
  };

  // 매물 제목 정리 (불필요한 글자 제거)
  const cleanPropertyTitle = title => {
    return title.replace(/숙/g, '').trim();
  };

  // 필터링 로직
  const getFilteredProperties = () => {
    let filtered = properties;

