interface HomePageProps {
  searchTerm?: string;
  addressSearch?: string;
  filters?: {
    type: string;
    propertyType: string;
    area: string;
    price: string;
    deposit: string;
  };
  onPropertyAdded?: (properties: Property[]) => void;
}

const HomePage: React.FC<HomePageProps> = ({ 
  searchTerm = '', 
  addressSearch = '',
  filters = { type: '', propertyType: '', area: '', price: '', deposit: '' },
  onPropertyAdded
}) => {
  const [selectedProperty, setSelectedProperty] = useState<Property | null>(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalProperty, setModalProperty] = useState<Property | null>(null);
  const [properties, setProperties] = useState<Property[]>(sampleProperties);
  const [selectedLocation, setSelectedLocation] = useState<{ lat: number; lng: number } | null>(null);
  const [selectedClusterProperties, setSelectedClusterProperties] = useState<Property[]>([]);
  const [selectedMarkerId, setSelectedMarkerId] = useState<string | null>(null);

  // 매물 수정 관련 상태
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [editingProperty, setEditingProperty] = useState<Property | null>(null);
  const [editForm, setEditForm] = useState({
    title: '',
    description: '',
    price: '',
    deposit: '',
    address: '',
    lat: '',
    lng: '',
    bedrooms: '',
    bathrooms: '',
    area: '',
    type: '',
    propertyType: '',
    contactName: '',
    contactPhone: '',
    contactEmail: '',
    confirmedDate: ''
  });

  // 사진 슬라이드 관련 상태
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [mainImageIndex, setMainImageIndex] = useState(0);

  // 사진 업로드 관련 상태
  const [uploadedImages, setUploadedImages] = useState<string[]>([]);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // 매물 추가 처리
  const handlePropertyAdded = (newProperties: Property[]) => {
    setProperties(prev => [...prev, ...newProperties]);
    if (onPropertyAdded) {
      onPropertyAdded(newProperties);
    }
  };

  // 매물 수정 시작
  const handleEditProperty = (property: Property) => {
    setEditingProperty(property);
    setEditForm({
      title: property.title,
      description: property.description,
      price: property.price.toString(),
      deposit: (property.deposit || 0).toString(),
      address: property.address,
      lat: property.location.lat.toString(),
      lng: property.location.lng.toString(),
      bedrooms: (property.bedrooms || 0).toString(),
      bathrooms: (property.bathrooms || 0).toString(),
      area: property.area.toString(),
      type: property.type,
      propertyType: property.propertyType,
      contactName: property.contact.name,
      contactPhone: property.contact.phone,
      contactEmail: property.contact.email,
      confirmedDate: property.confirmedDate || '25.07.19'
    });
    setIsEditModalOpen(true);
  };

  // 매물 수정 저장
  const handleSaveProperty = (e: React.FormEvent) => {
    e.preventDefault();

    if (!editingProperty) return;

    const updatedProperty: Property = {
      ...editingProperty,
      title: editForm.title,
      description: editForm.description,
      price: parseInt(editForm.price) || 0,
      deposit: parseInt(editForm.deposit) || 0,
      address: editForm.address,
      location: {
        lat: parseFloat(editForm.lat) || 0,
        lng: parseFloat(editForm.lng) || 0
      },
      bedrooms: parseInt(editForm.bedrooms) || 0,
      bathrooms: parseInt(editForm.bathrooms) || 0,
      area: parseFloat(editForm.area) || 0,
      type: editForm.type as 'sale' | 'rent',
      propertyType: editForm.propertyType as 'apartment' | 'house' | 'commercial' | 'land',
      contact: {
        name: editForm.contactName,
        phone: editForm.contactPhone,
        email: editForm.contactEmail
      },
      confirmedDate: editForm.confirmedDate
    };

    setProperties(prev => 
      prev.map(p => p.id === editingProperty.id ? updatedProperty : p)
    );

    setIsEditModalOpen(false);
    setEditingProperty(null);
    alert('매물이 성공적으로 수정되었습니다!');
  };

  // 매물 수정 취소
  const handleCancelEdit = () => {
    setIsEditModalOpen(false);
    setEditingProperty(null);
  };

  // 사진 슬라이드 관련 함수들
  const handleImageChange = (index: number) => {
    setCurrentImageIndex(index);
  };

  const handlePrevImage = () => {
    if (uploadedImages.length > 0) {
      setCurrentImageIndex(prev => 
        prev === 0 ? uploadedImages.length - 1 : prev - 1
      );
    }
  };

  const handleNextImage = () => {
    if (uploadedImages.length > 0) {
      setCurrentImageIndex(prev => 
        prev === uploadedImages.length - 1 ? 0 : prev + 1
      );
    }
  };

  const handleSetMainImage = (index: number) => {
    setMainImageIndex(index);
    // 실제로는 매물의 대표사진을 업데이트하는 로직이 필요
    alert(`대표사진이 ${index + 1}번째 사진으로 설정되었습니다.`);
  };

  // 매물 상세보기 열 때 사진 인덱스 초기화
  const handlePropertyCardClick = (property: Property) => {
    setModalProperty(property);
    setIsModalOpen(true);
    setCurrentImageIndex(0);
    setMainImageIndex(0);
    // 업로드된 이미지 초기화
    setUploadedImages([]);
  };

  const handleMarkerClick = (property: Property) => {
    // 해당 위치의 모든 매물을 찾기
    const nearbyProperties = properties.filter(p => 
      Math.abs(p.location.lat - property.location.lat) < 0.001 && 
      Math.abs(p.location.lng - property.location.lng) < 0.001
    );

    console.log(`마커 클릭: ${property.title} 위치의 매물 ${nearbyProperties.length}개 발견`);

    setSelectedProperty(property);
    // 지도 상태 변경 제거 - 사용자가 원하는 위치에서 계속 탐색할 수 있도록
    // setSelectedLocation(property.location);
    setSelectedClusterProperties([]); // 클러스터 선택 해제
    setSelectedMarkerId(property.id); // 마커 선택
    setIsSidebarOpen(true); // 사이드바 열기
  };

  const handleClusterClick = (clusterProperties: Property[]) => {
    console.log(`클러스터 클릭: ${clusterProperties.length}개 매물 선택`);

    setSelectedClusterProperties(clusterProperties);
    setSelectedProperty(null);
    // 지도 상태 변경 제거 - 사용자가 원하는 위치에서 계속 탐색할 수 있도록
    // setSelectedLocation(null);
    setIsSidebarOpen(true); // 사이드바 열기
  };

  const handleShowAllProperties = () => {
    setSelectedProperty(null);
    setSelectedLocation(null);
    setSelectedClusterProperties([]);
  };

  const formatPrice = (price: number) => {
    if (price >= 100000000) {
      return `${Math.floor(price / 100000000)}억원`;
    } else if (price >= 10000) {
      return `${Math.floor(price / 10000)}만원`;
    }
    return `${price.toLocaleString()}원`;
  };

  // 매물 제목 정리 (불필요한 글자 제거)
  const cleanPropertyTitle = (title: string) => {
    return title.replace(/숙/g, '').trim();
  };

  // 필터링 로직
  const getFilteredProperties = () => {
    let filtered = properties;

    // 검색어 필터링
    if (searchTerm) {
      filtered = filtered.filter(property => 
        property.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        property.id.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // 주소 검색 필터링
    if (addressSearch) {
      filtered = filtered.filter(property => 
        property.address.toLowerCase().includes(addressSearch.toLowerCase())
      );
    }

    // 필터 조건 적용
    if (filters.type) {
      filtered = filtered.filter(property => property.type === filters.type);
    }
    if (filters.propertyType) {
      filtered = filtered.filter(property => property.propertyType === filters.propertyType);
    }

    // 디버깅용 콘솔 로그
    console.log('현재 필터:', filters);
    console.log('면적 필터:', filters.area);
    console.log('금액 필터:', filters.price);
    console.log('보증금 필터:', filters.deposit);

    if (filters.area) {
      // 면적 범위 필터링 (새로운 형식: "~5평,10평" 또는 "10평,50평")
      const selectedAreas = filters.area.split(',');
      console.log('선택된 면적들:', selectedAreas);

      filtered = filtered.filter(property => {
        if (!property.area) return false;

        // 평을 m²로 변환 (1평 = 3.3058m²)
        const areaInPyeong = property.area / 3.3058;
        console.log(`매물 ${property.id}: ${property.area}m² = ${areaInPyeong.toFixed(1)}평`);

        // 선택된 면적이 2개인 경우 범위 검색
        if (selectedAreas.length === 2) {
          const minArea = selectedAreas[0];
          const maxArea = selectedAreas[1];

          let minValue = 0;
          let maxValue = 999;

          // 최소값 설정
          if (minArea === '~5평') {
            minValue = 0;
          } else {
            minValue = parseInt(minArea.replace(/[평~]/g, ''));
          }

          // 최대값 설정
          if (maxArea === '200평~') {
            maxValue = 999;
          } else {
            maxValue = parseInt(maxArea.replace(/[평~]/g, ''));
          }

          if (areaInPyeong >= minValue && areaInPyeong <= maxValue) {
            console.log(`매물 ${property.id} 필터 통과: ${areaInPyeong.toFixed(1)}평 (${minValue}평~${maxValue}평)`);
            return true;
          }
        } else {
          // 단일 선택인 경우
          for (const areaRange of selectedAreas) {
            if (areaRange === '~5평') {
              if (areaInPyeong <= 5) {
                console.log(`매물 ${property.id} 필터 통과: ${areaInPyeong.toFixed(1)}평 <= 5평`);
                return true;
              }
            } else if (areaRange === '200평~') {
              if (areaInPyeong >= 200) {
                console.log(`매물 ${property.id} 필터 통과: ${areaInPyeong.toFixed(1)}평 >= 200평`);
                return true;
              }
            } else {
              const areaValue = parseInt(areaRange.replace(/[평~]/g, ''));
              if (Math.abs(areaInPyeong - areaValue) < 1) {
                console.log(`매물 ${property.id} 필터 통과: ${areaInPyeong.toFixed(1)}평 ≈ ${areaValue}평`);
                return true; // 1평 오차 허용
              }
            }
          }
        }
        console.log(`매물 ${property.id} 필터 실패: ${areaInPyeong.toFixed(1)}평`);
        return false;
      });
    }
    if (filters.price) {
      // 가격 범위 필터링 (새로운 형식: "~1억,10억" 또는 "10억,50억")
      const selectedPrices = filters.price.split(',');
      console.log('선택된 금액들:', selectedPrices);

      filtered = filtered.filter(property => {
        const priceInHundredMillion = property.price / 100000000; // 원을 억 단위로 변환
        console.log(`매물 ${property.id}: ${property.price}원 = ${priceInHundredMillion.toFixed(1)}억원`);

        // 선택된 가격이 2개인 경우 범위 검색
        if (selectedPrices.length === 2) {
          const minPrice = selectedPrices[0];
          const maxPrice = selectedPrices[1];

          let minValue = 0;
          let maxValue = 999;

          // 최소값 설정
          if (minPrice === '~1억') {
            minValue = 0;
          } else {
            minValue = parseInt(minPrice.replace(/[억~]/g, ''));
          }

          // 최대값 설정
          if (maxPrice === '500억~') {
            maxValue = 999;
          } else {
            maxValue = parseInt(maxPrice.replace(/[억~]/g, ''));
          }

          if (priceInHundredMillion >= minValue && priceInHundredMillion <= maxValue) {
            console.log(`매물 ${property.id} 필터 통과: ${priceInHundredMillion.toFixed(1)}억원 (${minValue}억원~${maxValue}억원)`);
            return true;
          }
        } else {
          // 단일 선택인 경우
          for (const priceRange of selectedPrices) {
            if (priceRange === '~1억') {
              if (priceInHundredMillion <= 1) {
                console.log(`매물 ${property.id} 필터 통과: ${priceInHundredMillion.toFixed(1)}억원 <= 1억원`);
                return true;
              }
            } else if (priceRange === '500억~') {
              if (priceInHundredMillion >= 500) {
                console.log(`매물 ${property.id} 필터 통과: ${priceInHundredMillion.toFixed(1)}억원 >= 500억원`);
                return true;
              }
            } else {
              const priceValue = parseInt(priceRange.replace(/[억~]/g, ''));
              if (Math.abs(priceInHundredMillion - priceValue) < 0.1) {
                console.log(`매물 ${property.id} 필터 통과: ${priceInHundredMillion.toFixed(1)}억원 ≈ ${priceValue}억원`);
                return true; // 0.1억 오차 허용
              }
            }
          }
        }
        console.log(`매물 ${property.id} 필터 실패: ${priceInHundredMillion.toFixed(1)}억원`);
        return false;
      });
    }
    if (filters.deposit) {
      // 보증금 범위 필터링 (새로운 형식: "~100만,~500만" 또는 "~1천만,~2천만")
      const selectedDeposits = filters.deposit.split(',');
      console.log('선택된 보증금들:', selectedDeposits);

      filtered = filtered.filter(property => {
        if (!property.deposit) return false;
        const depositInMillion = property.deposit / 1000000; // 원을 백만 단위로 변환
        console.log(`매물 ${property.id}: ${property.deposit}원 = ${depositInMillion.toFixed(1)}백만원`);

        // 선택된 보증금이 2개인 경우 범위 검색
        if (selectedDeposits.length === 2) {
          const minDeposit = selectedDeposits[0];
          const maxDeposit = selectedDeposits[1];

          let minValue = 0;
          let maxValue = 999;

          // 최소값 설정
          if (minDeposit === '~100만') {
            minValue = 0;
          } else if (minDeposit === '~500만') {
            minValue = 0;
          } else if (minDeposit === '~1천만') {
            minValue = 0;
          } else if (minDeposit === '~2천만') {
            minValue = 0;
          } else if (minDeposit === '~3천만') {
            minValue = 0;
          } else if (minDeposit === '~4천만') {
            minValue = 0;
          }

          // 최대값 설정
          if (maxDeposit === '~100만') {
            maxValue = 0.1;
          } else if (maxDeposit === '~500만') {
            maxValue = 0.5;
          } else if (maxDeposit === '~1천만') {
            maxValue = 1;
          } else if (maxDeposit === '~2천만') {
            maxValue = 2;
          } else if (maxDeposit === '~3천만') {
            maxValue = 3;
          } else if (maxDeposit === '~4천만') {
            maxValue = 4;
          }

          if (depositInMillion >= minValue && depositInMillion <= maxValue) {
            console.log(`매물 ${property.id} 필터 통과: ${depositInMillion.toFixed(1)}백만원 (${minValue}백만원~${maxValue}백만원)`);
            return true;
          }
        } else {
          // 단일 선택인 경우
          for (const depositRange of selectedDeposits) {
            if (depositRange === '~100만') {
              if (depositInMillion <= 0.1) {
                console.log(`매물 ${property.id} 필터 통과: ${depositInMillion.toFixed(1)}백만원 <= 0.1백만원`);
                return true;
              }
            } else if (depositRange === '~500만') {
              if (depositInMillion <= 0.5) {
                console.log(`매물 ${property.id} 필터 통과: ${depositInMillion.toFixed(1)}백만원 <= 0.5백만원`);
                return true;
              }
            } else if (depositRange === '~1천만') {
              if (depositInMillion <= 1) {
                console.log(`매물 ${property.id} 필터 통과: ${depositInMillion.toFixed(1)}백만원 <= 1백만원`);
                return true;
              }
            } else if (depositRange === '~2천만') {
              if (depositInMillion <= 2) {
                console.log(`매물 ${property.id} 필터 통과: ${depositInMillion.toFixed(1)}백만원 <= 2백만원`);
                return true;
              }
            } else if (depositRange === '~3천만') {
              if (depositInMillion <= 3) {
                console.log(`매물 ${property.id} 필터 통과: ${depositInMillion.toFixed(1)}백만원 <= 3백만원`);
                return true;
              }
            } else if (depositRange === '~4천만') {
              if (depositInMillion <= 4) {
                console.log(`매물 ${property.id} 필터 통과: ${depositInMillion.toFixed(1)}백만원 <= 4백만원`);
                return true;
              }
            }
          }
        }
        console.log(`매물 ${property.id} 필터 실패: ${depositInMillion.toFixed(1)}백만원`);
        return false;
      });
    }

    return filtered;
  };

  const displayProperties = selectedClusterProperties.length > 0
    ? selectedClusterProperties
    : selectedLocation 
      ? properties.filter(p => 
          Math.abs(p.location.lat - selectedLocation.lat) < 0.001 && 
          Math.abs(p.location.lng - selectedLocation.lng) < 0.001
        )
      : getFilteredProperties();
  const filteredProperties = getFilteredProperties(); // 지도용 필터링된 매물

  // 사진 업로드 처리
  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      const newImages: string[] = [];

      Array.from(files).forEach((file, index) => {
        const reader = new FileReader();
        reader.onload = (event) => {
          const imageUrl = event.target?.result as string;
          newImages.push(imageUrl);

          // 모든 파일이 로드되면 상태 업데이트
          if (newImages.length === files.length) {
            setUploadedImages(prev => {
              const updatedImages = [...prev, ...newImages];
              // 첫 번째 업로드된 사진을 대표사진으로 설정
              if (prev.length === 0) {
                setMainImageIndex(0);
              }
              return updatedImages;
            });
          }
        };
        reader.readAsDataURL(file);
      });
    }
  };

  // 사진 업로드 버튼 클릭
  const handleUploadClick = () => {
    fileInputRef.current?.click();
  };

  return (
    <>
      <HomeContainer>
        <MapSection>
          <GoogleMap
            properties={filteredProperties}
            onMarkerClick={handleMarkerClick}
            onClusterClick={handleClusterClick}
            selectedMarkerId={selectedMarkerId}
            setSelectedMarkerId={setSelectedMarkerId}
          />
        </MapSection>
        <Sidebar isOpen={isSidebarOpen}>
          <ToggleButton 
            isOpen={isSidebarOpen}
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          >
            {isSidebarOpen ? '◀' : '▶'}
          </ToggleButton>
          {isSidebarOpen && (
            <>
              <SidebarTitle>
                {selectedClusterProperties.length > 0 
                  ? `선택된 클러스터 매물 (${selectedClusterProperties.length}개)`
                  : selectedLocation 
                    ? `선택된 위치 매물 (${displayProperties.length}개)` 
                    : '매물 목록'}
                {(selectedClusterProperties.length > 0 || selectedLocation) && (
                  <button 
                    onClick={handleShowAllProperties}
                    style={{
                      marginLeft: '1rem',
                      padding: '0.25rem 0.5rem',
                      fontSize: '0.75rem',
                      background: '#2563eb',
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                  >
                    전체보기
                  </button>
                )}
              </SidebarTitle>

              {!selectedLocation && !selectedClusterProperties.length && (
                <FilterInfo>
                  총 {properties.length}개 중 {filteredProperties.length}개 표시
                  {(searchTerm || addressSearch || Object.values(filters).some(f => f)) && (
                    <div style={{ marginTop: '0.25rem', fontSize: '0.75rem', color: '#94a3b8' }}>
                      필터 적용됨
                    </div>
                  )}
                </FilterInfo>
              )}

              {selectedLocation && !selectedClusterProperties.length && (
                <FilterInfo>
                  선택된 위치: {selectedLocation.lat.toFixed(4)}, {selectedLocation.lng.toFixed(4)}
                  <div style={{ marginTop: '0.25rem', fontSize: '0.75rem', color: '#94a3b8' }}>
                    해당 위치의 매물 {displayProperties.length}개
                  </div>
                </FilterInfo>
              )}

              {selectedClusterProperties.length > 0 && (
                <FilterInfo>
                  <div style={{ color: '#dc2626', fontWeight: 'bold', fontSize: '1rem' }}>
                    {selectedClusterProperties.length}개 매물만 표시
                  </div>
                  <div style={{ marginTop: '0.25rem', fontSize: '0.75rem', color: '#94a3b8' }}>
                    클러스터에서 선택된 매물들
                  </div>
                </FilterInfo>
              )}

              {displayProperties.map(property => (
                <PropertyCard
                  key={property.id}
                  onClick={() => handlePropertyCardClick(property)}
                >
                  <PropertyCardImage>
                    <PropertyCardImageElement 
                      src={property.images[mainImageIndex] || property.images[0]} 
                      alt={property.title}
                      onError={(e) => {
                        const target = e.target as HTMLImageElement;
                        target.src = 'https://via.placeholder.com/300x200?text=No+Image';
                      }}
                    />
                  </PropertyCardImage>
                  <PropertyCardHeader>
                    <PropertyCardContent>
                      <PropertyCardNumber>매물 번호: {property.id}</PropertyCardNumber>
                      <PropertyCardTitle>{cleanPropertyTitle(property.title)}</PropertyCardTitle>
                      <PropertyPrice>{formatPrice(property.price)}</PropertyPrice>
                      <PropertyDetails>
                        {property.bedrooms && `${property.bedrooms}침실`}
                        {property.bathrooms && ` ${property.bathrooms}욕실`}
                        {property.area && ` ${property.area}㎡`}
                      </PropertyDetails>
                      <PropertyAddress>{property.address}</PropertyAddress>
                    </PropertyCardContent>
                    <ButtonContainer>
                      <EditButton 
                        onClick={(e) => {
                          e.stopPropagation();
                          handleEditProperty(property);
                        }}
                      >
                        수정
                      </EditButton>
                      <DeleteButton 
                        onClick={(e) => {
                          e.stopPropagation();
                          if (window.confirm(`매물 "${property.title}"을(를) 삭제하시겠습니까?`)) {
                            setProperties(prev => prev.filter(p => p.id !== property.id));
                            alert('매물이 삭제되었습니다.');
                          }
                        }}
                      >
                        삭제
                      </DeleteButton>
                    </ButtonContainer>
                  </PropertyCardHeader>
                </PropertyCard>
              ))}
            </>
          )}
        </Sidebar>
      </HomeContainer>

      <PropertyDetailModal isOpen={isModalOpen} onClick={() => setIsModalOpen(false)}>
        <ModalContent onClick={(e) => e.stopPropagation()}>
          <CloseButton onClick={() => setIsModalOpen(false)}>&times;</CloseButton>
          {modalProperty && (
            <>
              <PropertyHeader>
                <PropertyId>매물 번호: {modalProperty.id}</PropertyId>
              </PropertyHeader>
              <PropertyTitle>{cleanPropertyTitle(modalProperty.title)}</PropertyTitle>

              <PropertyImages>
                <ImageSlider onClick={handleUploadClick}>
                  {uploadedImages.length > 0 ? (
                    <MainImage 
                      src={uploadedImages[currentImageIndex]} 
                      alt={modalProperty.title} 
                    />
                  ) : (
                    <>
                      <UploadIcon>📷</UploadIcon>
                      <UploadText>사진을 업로드하세요</UploadText>
                    </>
                  )}
                  {uploadedImages.length > 0 && (
                    <>
                      <ImageNavigation>
                        <NavButton 
                          onClick={handlePrevImage} 
                          disabled={uploadedImages.length <= 1}
                        >
                          ←
                        </NavButton>
                        <NavButton 
                          onClick={handleNextImage} 
                          disabled={uploadedImages.length <= 1}
                        >
                          →
                        </NavButton>
                      </ImageNavigation>
                      <ImageCounter>
                        {currentImageIndex + 1}/{uploadedImages.length}
                      </ImageCounter>
                    </>
                  )}
                </ImageSlider>
                {uploadedImages.length > 0 && (
                  <ThumbnailGrid>
                    {uploadedImages.map((image, index) => (
                      <Thumbnail
                        key={index}
                        onClick={() => handleImageChange(index)}
                        isActive={index === currentImageIndex}
                        isMain={index === mainImageIndex}
                      >
                        <ThumbnailImage 
                          src={image} 
                          alt={`${modalProperty.title} 썸네일 ${index + 1}`} 
                        />
                        {index === mainImageIndex && (
                          <MainImageBadge>대표</MainImageBadge>
                        )}
                      </Thumbnail>
                    ))}
                  </ThumbnailGrid>
                )}
                <HiddenFileInput 
                  ref={fileInputRef}
                  type="file" 
                  multiple 
                  accept="image/*" 
                  onChange={handleImageUpload} 
                />
              </PropertyImages>

              <PropertyDetailsGrid>
                <DetailSection>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
                    <ConfirmedPropertyBadge>
                      <BadgeTop>확인매물</BadgeTop>
                      <BadgeBottom>{modalProperty.confirmedDate || '25.07.19'}</BadgeBottom>
                    </ConfirmedPropertyBadge>
                    <MainSectionTitle style={{ margin: 0, fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937' }}>매물정보</MainSectionTitle>
                  </div>
                  <DetailItem>
                    <DetailLabel>주소</DetailLabel>
                    <DetailValue>{modalProperty.address}</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>매물종류</DetailLabel>
                    <DetailValue>
                      {modalProperty.propertyType === 'apartment' ? '아파트' :
                       modalProperty.propertyType === 'house' ? '단독주택' :
                       modalProperty.propertyType === 'commercial' ? '상가' : '토지'}
                    </DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>거래유형</DetailLabel>
                    <DetailValue>{modalProperty.type === 'sale' ? '매매' : '임대'}</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>매매가</DetailLabel>
                    <DetailValue>{formatPrice(modalProperty.price)}</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>보증금/임대료</DetailLabel>
                    <DetailValue>{formatPrice(modalProperty.deposit || 0)}</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>관리비</DetailLabel>
                    <DetailValue>20만원</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>융자금</DetailLabel>
                    <DetailValue>-</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>공급/전용면적</DetailLabel>
                    <DetailValue>{modalProperty.area}㎡</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>건축물용도</DetailLabel>
                    <DetailValue>주거용</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>해당층/전체층</DetailLabel>
                    <DetailValue>5층/15층</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>방/화장실</DetailLabel>
                    <DetailValue>{modalProperty.bedrooms}방{modalProperty.bathrooms}욕</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>난방유형</DetailLabel>
                    <DetailValue>개별난방</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>주차</DetailLabel>
                    <DetailValue>가능</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>엘리베이터</DetailLabel>
                    <DetailValue>있음</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>사용승인일</DetailLabel>
                    <DetailValue>2023-01-01</DetailValue>
                  </DetailItem>
                  <DetailItem>
                    <DetailLabel>입주가능일</DetailLabel>
                    <DetailValue>즉시입주</DetailValue>
                  </DetailItem>
                </DetailSection>
                <RightSection>
                  <div style={{ marginTop: '6rem' }}>
                    <SectionTitle style={{ fontSize: '0.9rem', fontWeight: '600', marginBottom: '1rem' }}>매물설명</SectionTitle>
                    <div style={{ 
                      padding: '1rem', 
                      background: '#f9fafb', 
                      borderRadius: '4px', 
                      height: 'fit-content',
                      lineHeight: '1.6',
                      fontSize: '0.875rem',
                      color: '#374151',
                      marginBottom: '1rem'
                    }}>
                      {modalProperty.description}
                    </div>
                  </div>

                  <div>
                    <SectionTitle style={{ fontSize: '0.9rem', fontWeight: '600', marginBottom: '1rem' }}>연락처</SectionTitle>
                    <ContactItem>
                      <span>📞</span>
                      <span>{modalProperty.contact.name}</span>
                    </ContactItem>
                    <ContactItem>
                      <span>📱</span>
                      <span>{modalProperty.contact.phone}</span>
                    </ContactItem>
                    <ContactItem>
                      <span>✉️</span>
                      <span>{modalProperty.contact.email}</span>
                    </ContactItem>
                  </div>
                </RightSection>
              </PropertyDetailsGrid>

              <div style={{ marginTop: '2rem' }}>
                <DetailSection>
                  <DetailItem>
                    <DetailLabel style={{ fontSize: '1.1rem', fontWeight: '600' }}>주소</DetailLabel>
                    <DetailValue style={{ fontSize: '1.1rem', fontWeight: '600' }}>{modalProperty.address}</DetailValue>
                  </DetailItem>
                  <MapContainer>
                    <GoogleMap
                      properties={[modalProperty]}
                      onMarkerClick={() => {}}
                      center={modalProperty.location}
                      zoom={15}
                    />
                  </MapContainer>
                </DetailSection>
              </div>
            </>
          )}
        </ModalContent>
      </PropertyDetailModal>

      <EditModal isOpen={isEditModalOpen} onClick={handleCancelEdit}>
        <EditModalContent onClick={(e) => e.stopPropagation()}>
          <CloseButton onClick={handleCancelEdit}>&times;</CloseButton>
          <ModalTitle>매물 수정</ModalTitle>
          <EditForm onSubmit={handleSaveProperty}>
            <FormGroup>
              <FormLabel>제목</FormLabel>
              <FormInput
                type="text"
                value={editForm.title}
                onChange={(e) => setEditForm(prev => ({ ...prev, title: e.target.value }))}
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>설명</FormLabel>
              <FormTextarea
                value={editForm.description}
                onChange={(e) => setEditForm(prev => ({ ...prev, description: e.target.value }))}
                rows={4}
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>가격</FormLabel>
              <FormInput
                type="number"
                value={editForm.price}
                onChange={(e) => setEditForm(prev => ({ ...prev, price: e.target.value }))}
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>보증금</FormLabel>
              <FormInput
                type="number"
                value={editForm.deposit}
                onChange={(e) => setEditForm(prev => ({ ...prev, deposit: e.target.value }))}
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>주소</FormLabel>
              <FormInput
                type="text"
                value={editForm.address}
                onChange={(e) => setEditForm(prev => ({ ...prev, address: e.target.value }))}
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>위도</FormLabel>
              <FormInput
                type="number"
                value={editForm.lat}
                onChange={(e) => setEditForm(prev => ({ ...prev, lat: e.target.value }))}
                step="any"
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>경도</FormLabel>
              <FormInput
                type="number"
                value={editForm.lng}
                onChange={(e) => setEditForm(prev => ({ ...prev, lng: e.target.value }))}
                step="any"
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>침실</FormLabel>
              <FormInput
                type="number"
                value={editForm.bedrooms}
                onChange={(e) => setEditForm(prev => ({ ...prev, bedrooms: e.target.value }))}
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>욕실</FormLabel>
              <FormInput
                type="number"
                value={editForm.bathrooms}
                onChange={(e) => setEditForm(prev => ({ ...prev, bathrooms: e.target.value }))}
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>면적 (㎡)</FormLabel>
              <FormInput
                type="number"
                value={editForm.area}
                onChange={(e) => setEditForm(prev => ({ ...prev, area: e.target.value }))}
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>거래 유형</FormLabel>
              <FormSelect
                value={editForm.type}
                onChange={(e) => setEditForm(prev => ({ ...prev, type: e.target.value }))}
              >
                <option value="">선택하세요</option>
                <option value="sale">매매</option>
                <option value="rent">임대</option>
              </FormSelect>
            </FormGroup>
            <FormGroup>
              <FormLabel>매물 유형</FormLabel>
              <FormSelect
                value={editForm.propertyType}
                onChange={(e) => setEditForm(prev => ({ ...prev, propertyType: e.target.value }))}
              >
                <option value="">선택하세요</option>
                <option value="apartment">아파트</option>
                <option value="house">단독주택</option>
                <option value="commercial">상가</option>
                <option value="land">토지</option>
              </FormSelect>
            </FormGroup>
            <FormGroup>
              <FormLabel>연락처 이름</FormLabel>
              <FormInput
                type="text"
                value={editForm.contactName}
                onChange={(e) => setEditForm(prev => ({ ...prev, contactName: e.target.value }))}
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>연락처 전화번호</FormLabel>
              <FormInput
                type="tel"
                value={editForm.contactPhone}
                onChange={(e) => setEditForm(prev => ({ ...prev, contactPhone: e.target.value }))}
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>연락처 이메일</FormLabel>
              <FormInput
                type="email"
                value={editForm.contactEmail}
                onChange={(e) => setEditForm(prev => ({ ...prev, contactEmail: e.target.value }))}
                required
              />
            </FormGroup>
            <FormGroup>
              <FormLabel>확인매물 날짜</FormLabel>
              <FormInput
                type="text"
                value={editForm.confirmedDate}
                onChange={(e) => setEditForm(prev => ({ ...prev, confirmedDate: e.target.value }))}
                placeholder="예: 25.07.19"
              />
            </FormGroup>
            <ButtonGroup>
              <SaveButton type="submit">저장</SaveButton>
              <CancelButton onClick={handleCancelEdit}>취소</CancelButton>
            </ButtonGroup>
          </EditForm>
        </EditModalContent>
      </EditModal>
    </>
  );
};

export default HomePage;
