interface HomePageProps {
  searchTerm?: string;
  addressSearch?: string;
  filters?: {
    type: string;
    propertyType: string;
    area: string;
    price: string;
    deposit: string;
  };
}

const HomePage: React.FC<HomePageProps> = ({ 
  searchTerm = '', 
  addressSearch = '',
  filters = { type: '', propertyType: '', area: '', price: '', deposit: '' }
}) => {
  const [properties] = useState<Property[]>(sampleProperties);
  const [selectedProperty, setSelectedProperty] = useState<Property | null>(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalProperty, setModalProperty] = useState<Property | null>(null);

  const handleMarkerClick = (property: Property) => {
    setSelectedProperty(property);
    setIsSidebarOpen(true); // 사이드바 열기
  };

  const handlePropertyCardClick = (property: Property) => {
    setModalProperty(property);
    setIsModalOpen(true);
  };

  const handleShowAllProperties = () => {
    setSelectedProperty(null);
  };

  const formatPrice = (price: number) => {
    if (price >= 100000000) {
      return `${Math.floor(price / 100000000)}억원`;
    } else if (price >= 10000) {
      return `${Math.floor(price / 10000)}만원`;
    }
    return `${price.toLocaleString()}원`;
  };

  // 필터링 로직
  const getFilteredProperties = () => {
    let filtered = properties;

    // 검색어 필터링
    if (searchTerm) {
      filtered = filtered.filter(property => 
        property.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        property.id.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // 주소 검색 필터링
    if (addressSearch) {
      filtered = filtered.filter(property => 
        property.address.toLowerCase().includes(addressSearch.toLowerCase())
      );
    }

    // 필터 조건 적용
    if (filters.type) {
      filtered = filtered.filter(property => property.type === filters.type);
    }
    if (filters.propertyType) {
      filtered = filtered.filter(property => property.propertyType === filters.propertyType);
    }
    if (filters.area) {
      // 면적 범위 필터링 (새로운 형식: "~5평,10평" 또는 "10평,50평")
      const selectedAreas = filters.area.split(',');
      filtered = filtered.filter(property => {
        if (!property.area) return false;
        
        // 선택된 면적 범위 확인
        for (const areaRange of selectedAreas) {
          if (areaRange === '~5평') {
            if (property.area <= 5) return true;
          } else if (areaRange === '200평~') {
            if (property.area >= 200) return true;
          } else {
            const areaValue = parseInt(areaRange.replace(/[평~]/g, ''));
            if (property.area === areaValue) return true;
          }
        }
        return false;
      });
    }
    if (filters.price) {
      // 가격 범위 필터링 (새로운 형식: "~1억,10억" 또는 "10억,50억")
      const selectedPrices = filters.price.split(',');
      filtered = filtered.filter(property => {
        const priceInHundredMillion = property.price / 100000000; // 원을 억 단위로 변환
        
        // 선택된 가격 범위 확인
        for (const priceRange of selectedPrices) {
          if (priceRange === '~1억') {
            if (priceInHundredMillion <= 1) return true;
          } else if (priceRange === '500억~') {
            if (priceInHundredMillion >= 500) return true;
          } else {
            const priceValue = parseInt(priceRange.replace(/[억~]/g, ''));
            if (priceInHundredMillion === priceValue) return true;
          }
        }
        return false;
      });
    }
    if (filters.deposit) {
      // 보증금 범위 필터링 (새로운 형식: "~100만,~500만" 또는 "~1천만,~2천만")
      const selectedDeposits = filters.deposit.split(',');
      filtered = filtered.filter(property => {
        if (!property.deposit) return false;
        const depositInMillion = property.deposit / 1000000; // 원을 백만 단위로 변환
        
        // 선택된 보증금 범위 확인
        for (const depositRange of selectedDeposits) {
          if (depositRange === '~100만') {
            if (depositInMillion <= 0.1) return true;
          } else if (depositRange === '~500만') {
            if (depositInMillion <= 0.5) return true;
          } else if (depositRange === '~1천만') {
            if (depositInMillion <= 1) return true;
          } else if (depositRange === '~2천만') {
            if (depositInMillion <= 2) return true;
          } else if (depositRange === '~3천만') {
            if (depositInMillion <= 3) return true;
          } else if (depositRange === '~4천만') {
            if (depositInMillion <= 4) return true;
          }
        }
        return false;
      });
    }

    return filtered;
  };

  const displayProperties = selectedProperty ? [selectedProperty] : getFilteredProperties();
  const filteredProperties = getFilteredProperties(); // 지도용 필터링된 매물

  return (
    <>
      <HomeContainer>
        <MapSection>
          <GoogleMap
            properties={filteredProperties}
            onMarkerClick={handleMarkerClick}
          />
        </MapSection>
        <Sidebar isOpen={isSidebarOpen}>
          <ToggleButton 
            isOpen={isSidebarOpen}
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          >
            {isSidebarOpen ? '◀' : '▶'}
          </ToggleButton>
          {isSidebarOpen && (
            <>
              <SidebarTitle>
                {selectedProperty ? '선택된 매물' : '매물 목록'}
                {selectedProperty && (
                  <button 
                    onClick={handleShowAllProperties}
                    style={{
                      marginLeft: '1rem',
                      padding: '0.25rem 0.5rem',
                      fontSize: '0.75rem',
                      background: '#2563eb',
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                  >
                    전체보기
                  </button>
                )}
              </SidebarTitle>

              {!selectedProperty && (
                <FilterInfo>
                  총 {properties.length}개 중 {filteredProperties.length}개 표시
                  {(searchTerm || addressSearch || Object.values(filters).some(f => f)) && (
                    <div style={{ marginTop: '0.25rem', fontSize: '0.75rem', color: '#94a3b8' }}>
                      필터 적용됨
                    </div>
                  )}
                </FilterInfo>
              )}

              {displayProperties.map(property => (
                <PropertyCard
                  key={property.id}
                  onClick={() => handlePropertyCardClick(property)}
                >
                  <PropertyTitle>{property.title}</PropertyTitle>
                  <PropertyPrice>{formatPrice(property.price)}</PropertyPrice>
                  <PropertyDetails>
                    {property.bedrooms && `${property.bedrooms}침실`}
                    {property.bathrooms && ` ${property.bathrooms}욕실`}
                    {property.area && ` ${property.area}㎡`}
                  </PropertyDetails>
                  <PropertyAddress>{property.address}</PropertyAddress>
                </PropertyCard>
              ))}
            </>
          )}
        </Sidebar>
      </HomeContainer>
      
      <PropertyDetailModal isOpen={isModalOpen} onClick={() => setIsModalOpen(false)}>
        <ModalContent onClick={(e) => e.stopPropagation()}>
          <CloseButton onClick={() => setIsModalOpen(false)}>&times;</CloseButton>
          {modalProperty && (
            <>
              <ModalTitle>{modalProperty.title}</ModalTitle>
              <ModalPrice>{formatPrice(modalProperty.price)}</ModalPrice>
              <ModalAddress>{modalProperty.address}</ModalAddress>
              <ModalDescription>{modalProperty.description}</ModalDescription>
              
              <ModalInfo>
                <InfoItem>
                  <InfoLabel>거래 유형</InfoLabel>
                  <InfoValue>{modalProperty.type === 'sale' ? '매매' : '임대'}</InfoValue>
                </InfoItem>
                <InfoItem>
                  <InfoLabel>매물 유형</InfoLabel>
                  <InfoValue>
                    {modalProperty.propertyType === 'apartment' ? '아파트' :
                     modalProperty.propertyType === 'house' ? '단독주택' :
                     modalProperty.propertyType === 'commercial' ? '상가' : '토지'}
                  </InfoValue>
                </InfoItem>
                {modalProperty.bedrooms && (
                  <InfoItem>
                    <InfoLabel>침실</InfoLabel>
                    <InfoValue>{modalProperty.bedrooms}개</InfoValue>
                  </InfoItem>
                )}
                {modalProperty.bathrooms && (
                  <InfoItem>
                    <InfoLabel>욕실</InfoLabel>
                    <InfoValue>{modalProperty.bathrooms}개</InfoValue>
                  </InfoItem>
                )}
                {modalProperty.area && (
                  <InfoItem>
                    <InfoLabel>면적</InfoLabel>
                    <InfoValue>{modalProperty.area}㎡</InfoValue>
                  </InfoItem>
                )}
              </ModalInfo>

              <ContactInfo>
                <ContactTitle>연락처</ContactTitle>
                <ContactItem>
                  <span>📞</span>
                  <span>{modalProperty.contact.name}</span>
                </ContactItem>
                <ContactItem>
                  <span>📱</span>
                  <span>{modalProperty.contact.phone}</span>
                </ContactItem>
                <ContactItem>
                  <span>✉️</span>
                  <span>{modalProperty.contact.email}</span>
                </ContactItem>
              </ContactInfo>
            </>
          )}
        </ModalContent>
      </PropertyDetailModal>
    </>
  );
};

export default HomePage;
