import React from 'react';
import styled from 'styled-components';

const PopupOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 2000;
`;

const PopupContent = styled.div`
  background: white;
  border-radius: 8px;
  padding: 1.5rem;
  min-width: 300px;
  max-width: 400px;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
  position: relative;
`;

const PopupTitle = styled.h3`
  font-size: 1.25rem;
  font-weight: bold;
  color: #1f2937;
  margin-bottom: 1rem;
  text-align: center;
`;

const OptionList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const OptionButton = styled.button<{ isSelected?: boolean }>`
  padding: 0.75rem 1rem;
  border: 1px solid ${props => props.isSelected ? '#2563eb' : '#d1d5db'};
  border-radius: 6px;
  background: ${props => props.isSelected ? '#2563eb' : 'white'};
  color: ${props => props.isSelected ? 'white' : '#374151'};
  font-size: 0.875rem;
  cursor: pointer;
  transition: all 0.2s ease;
  text-align: left;

  &:hover {
    background: ${props => props.isSelected ? '#1d4ed8' : '#f9fafb'};
    border-color: ${props => props.isSelected ? '#1d4ed8' : '#2563eb'};
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 0.5rem;
  margin-top: 1.5rem;
`;

const Button = styled.button<{ variant: 'primary' | 'secondary' }>`
  flex: 1;
  padding: 0.75rem;
  border-radius: 6px;
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  border: none;
  color: white;
  background: ${props => props.variant === 'primary' ? '#2563eb' : '#6b7280'};

  &:hover {
    background: ${props => props.variant === 'primary' ? '#1d4ed8' : '#4b5563'};
  }
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #6b7280;
  
  &:hover {
    color: #374151;
  }
`;

interface FilterPopupProps {
  isOpen: boolean;
  onClose: () => void;
  title: string;
  options: { value: string; label: string }[];
  selectedValue: string;
  onSelect: (value: string) => void;
  onApply: () => void;
  onClear: () => void;
}

const FilterPopup: React.FC<FilterPopupProps> = ({
  isOpen,
  onClose,
  title,
  options,
  selectedValue,
  onSelect,
  onApply,
  onClear
}) => {
  if (!isOpen) return null;

  return (
    <PopupOverlay onClick={onClose}>
      <PopupContent onClick={(e) => e.stopPropagation()}>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <PopupTitle>{title}</PopupTitle>
        
        <OptionList>
          {options.map((option) => (
            <OptionButton
              key={option.value}
              isSelected={selectedValue === option.value}
              onClick={() => onSelect(option.value)}
            >
              {option.label}
            </OptionButton>
          ))}
        </OptionList>

        <ButtonGroup>
          <Button variant="secondary" onClick={onClear}>
            초기화
          </Button>
          <Button variant="primary" onClick={onApply}>
            적용
          </Button>
        </ButtonGroup>
      </PopupContent>
    </PopupOverlay>
  );
};

export default FilterPopup;
