import React, { useState, useRef } from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 12px;
  padding: 2rem;
  width: 90%;
  max-width: 600px;
  max-height: 90vh;
  overflow-y: auto;
  box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid #e5e7eb;
`;

const ModalTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1f2937;
  margin: 0;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #6b7280;
  padding: 0.5rem;
  border-radius: 4px;
  transition: all 0.2s;

  &:hover {
    background: #f3f4f6;
    color: #374151;
  }
`;

const UploadSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const FileUploadArea = styled.div<{ isDragOver: boolean; hasFile: boolean }>`
  border: 2px dashed ${props => props.isDragOver ? '#2563eb' : props.hasFile ? '#10b981' : '#d1d5db'};
  border-radius: 8px;
  padding: 2rem;
  text-align: center;
  background: ${props => props.isDragOver ? '#eff6ff' : props.hasFile ? '#f0fdf4' : '#f9fafb'};
  transition: all 0.2s ease;
  cursor: pointer;

  &:hover {
    border-color: #2563eb;
    background: #eff6ff;
  }
`;

const UploadIcon = styled.div`
  font-size: 3rem;
  color: #6b7280;
  margin-bottom: 1rem;
`;

const UploadText = styled.p`
  font-size: 1rem;
  color: #374151;
  margin-bottom: 0.5rem;
`;

const UploadSubtext = styled.p`
  font-size: 0.875rem;
  color: #6b7280;
  margin: 0;
`;

const FileInput = styled.input`
  display: none;
`;

const FileInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem;
  background: #f0fdf4;
  border: 1px solid #10b981;
  border-radius: 6px;
  margin-top: 1rem;
`;

const FileIcon = styled.span`
  font-size: 1.5rem;
  color: #10b981;
`;

const FileName = styled.span`
  font-size: 0.875rem;
  color: #065f46;
  font-weight: 500;
`;

const FileSize = styled.span`
  font-size: 0.75rem;
  color: #6b7280;
`;

const TemplateSection = styled.div`
  border: 1px solid #e5e7eb;
  border-radius: 8px;
  padding: 1rem;
  background: #f9fafb;
  margin-bottom: 1rem;
`;

const TemplateTitle = styled.h3`
  font-size: 1rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.5rem;
`;

const TemplateDescription = styled.p`
  font-size: 0.875rem;
  color: #6b7280;
  margin-bottom: 1rem;
`;

const TemplateButton = styled.button`
  background: #10b981;
  color: white;
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 6px;
  font-size: 0.875rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  box-shadow: 0 2px 4px rgba(16, 185, 129, 0.2);

  &:hover {
    background: #059669;
    transform: translateY(-1px);
    box-shadow: 0 4px 8px rgba(16, 185, 129, 0.3);
  }

  &:active {
    transform: translateY(0);
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1.5rem;
`;

const Button = styled.button`
  background: #2563eb;
  color: white;
  padding: 1rem 2rem;
  border-radius: 6px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background: #1d4ed8;
  }

  &:disabled {
    background: #9ca3af;
    cursor: not-allowed;
  }
`;

const CancelButton = styled.button`
  background: #6b7280;
  color: white;
  padding: 1rem 2rem;
  border-radius: 6px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background: #4b5563;
  }
`;

const ProgressSection = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background: #f0fdf4;
  border: 1px solid #10b981;
  border-radius: 6px;
  display: none;

  &.visible {
    display: block;
  }
`;

const ProgressText = styled.p`
  font-size: 0.875rem;
  color: #065f46;
  margin: 0;
`;

const AddressSearchSection = styled.div`
  border: 1px solid #e5e7eb;
  border-radius: 8px;
  padding: 1rem;
  background: #f9fafb;
  margin-bottom: 1rem;
`;

const AddressSearchTitle = styled.h3`
  font-size: 1rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.5rem;
`;

const AddressSearchDescription = styled.p`
  font-size: 0.875rem;
  color: #6b7280;
  margin-bottom: 1rem;
`;

const AddressSearchInput = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 0.875rem;
  margin-bottom: 0.5rem;

  &:focus {
    outline: none;
    border-color: #2563eb;
    box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
  }
`;

const AddressSearchButton = styled.button`
  background: #2563eb;
  color: white;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 6px;
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background: #1d4ed8;
  }

  &:disabled {
    background: #9ca3af;
    cursor: not-allowed;
  }
`;

const CoordinateResult = styled.div`
  margin-top: 0.5rem;
  padding: 0.5rem;
  background: #f0fdf4;
  border: 1px solid #10b981;
  border-radius: 4px;
  font-size: 0.75rem;
  color: #065f46;
`;

interface AddPropertyModalProps {
  onClose: () => void;
}

const AddPropertyModal: React.FC<AddPropertyModalProps> = ({ onClose }) => {
  const [isDragOver, setIsDragOver] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // ì£¼ì†Œê²€ìƒ‰ ê´€ë ¨ ìƒíƒœ
  const [addressSearch, setAddressSearch] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [searchResult, setSearchResult] = useState<{ lat: number; lng: number } | null>(null);

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' || 
          file.type === 'application/vnd.ms-excel' ||
          file.name.endsWith('.xlsx') || 
          file.name.endsWith('.xls')) {
        setSelectedFile(file);
      } else {
        alert('ì—‘ì…€ íŒŒì¼(.xlsx, .xls)ë§Œ ì—…ë¡œë“œ ê°€ëŠ¥í•©ë‹ˆë‹¤.');
      }
    }
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      const file = files[0];
      if (file.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' || 
          file.type === 'application/vnd.ms-excel' ||
          file.name.endsWith('.xlsx') || 
          file.name.endsWith('.xls')) {
        setSelectedFile(file);
      } else {
        alert('ì—‘ì…€ íŒŒì¼(.xlsx, .xls)ë§Œ ì—…ë¡œë“œ ê°€ëŠ¥í•©ë‹ˆë‹¤.');
      }
    }
  };

  const handleUploadClick = () => {
    fileInputRef.current?.click();
  };

  const downloadTemplate = () => {
    // ì—‘ì…€ í…œí”Œë¦¿ ë‹¤ìš´ë¡œë“œ ë¡œì§
    const templateData = [
      ['ë§¤ë¬¼ë²ˆí˜¸', 'ë§¤ë¬¼ì œëª©', 'ë§¤ë¬¼ì„¤ëª…', 'í™•ì¸ë§¤ë¬¼', 'ì£¼ì†Œ', 'ìœ„ë„', 'ê²½ë„', 'ë§¤ë¬¼ì¢…ë¥˜', 'ê±°ë˜ìœ í˜•', 'ë§¤ë§¤ê°€', 'ë³´ì¦ê¸ˆ/ì„ëŒ€ë£Œ', 'ê´€ë¦¬ë¹„', 'ìœµìê¸ˆ', 'ê³µê¸‰/ì „ìš©ë©´ì ', 'ê±´ì¶•ë¬¼ìš©ë„', 'í•´ë‹¹ì¸µ/ì „ì²´ì¸µ', 'ë°©/í™”ì¥ì‹¤', 'ë‚œë°©ìœ í˜•', 'ì£¼ì°¨', 'ì—˜ë¦¬ë² ì´í„°', 'ì‚¬ìš©ìŠ¹ì¸ì¼', 'ì…ì£¼ê°€ëŠ¥ì¼'],
      ['P001', 'ê°•ë‚¨êµ¬ ì‹ ì¶• ì•„íŒŒíŠ¸', 'ê°•ë‚¨êµ¬ ì‹ ì¶• ì•„íŒŒíŠ¸ì…ë‹ˆë‹¤', 'í™•ì¸ë§¤ë¬¼', 'ì„œìš¸ì‹œ ê°•ë‚¨êµ¬', '37.5665', '126.9780', 'ì•„íŒŒíŠ¸', 'ë§¤ë§¤', '5ì–µì›', '', '20ë§Œì›', '', '50í‰/45í‰', 'ì£¼ê±°ìš©', '5ì¸µ/15ì¸µ', '3ë°©2ìš•', 'ê°œë³„ë‚œë°©', 'ê°€ëŠ¥', 'ìˆìŒ', '2023-01-01', 'ì¦‰ì‹œì…ì£¼'],
      ['P002', 'í™ëŒ€ì…êµ¬ ê·¼ì²˜ ì›ë£¸', 'í™ëŒ€ì…êµ¬ ê·¼ì²˜ ì›ë£¸ì…ë‹ˆë‹¤', 'í™•ì¸ë§¤ë¬¼', 'ì„œìš¸ì‹œ ë§ˆí¬êµ¬', '37.5575', '126.9250', 'ì›ë£¸', 'ì„ëŒ€', '', 'ë³´ì¦ê¸ˆ 1000ë§Œì›/ì›”ì„¸ 50ë§Œì›', '5ë§Œì›', '', '20í‰/18í‰', 'ì£¼ê±°ìš©', '3ì¸µ/5ì¸µ', '1ë°©1ìš•', 'ê°œë³„ë‚œë°©', 'ë¶ˆê°€ëŠ¥', 'ì—†ìŒ', '2020-05-01', 'ì¦‰ì‹œì…ì£¼']
    ];

    // CSV í˜•ì‹ìœ¼ë¡œ ë°ì´í„° ìƒì„± (ì—‘ì…€ì—ì„œ ì—´ ìˆ˜ ìˆë„ë¡)
    let csvContent = "data:text/csv;charset=utf-8,\uFEFF";
    templateData.forEach(row => {
      // ì‰¼í‘œê°€ í¬í•¨ëœ ë°ì´í„°ëŠ” ë”°ì˜´í‘œë¡œ ê°ì‹¸ê¸°
      const escapedRow = row.map(cell => {
        if (cell.includes(',') || cell.includes('"') || cell.includes('\n')) {
          return `"${cell.replace(/"/g, '""')}"`;
        }
        return cell;
      });
      csvContent += escapedRow.join(',') + '\r\n';
    });

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement('a');
    link.setAttribute('href', encodedUri);
    link.setAttribute('download', 'ë§¤ë¬¼_ë“±ë¡_í…œí”Œë¦¿.xlsx');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('íŒŒì¼ì„ ì„ íƒí•´ì£¼ì„¸ìš”.');
      return;
    }

    setIsUploading(true);
    setUploadProgress(0);

    // ì‹œë®¬ë ˆì´ì…˜ëœ ì—…ë¡œë“œ ì§„í–‰ë¥ 
    const interval = setInterval(() => {
      setUploadProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setIsUploading(false);
          alert('ë§¤ë¬¼ ë“±ë¡ì´ ì™„ë£Œë˜ì—ˆìŠµë‹ˆë‹¤!');
          onClose();
          return 100;
        }
        return prev + 10;
      });
    }, 200);

    // ì‹¤ì œ êµ¬í˜„ì—ì„œëŠ” ì—¬ê¸°ì— íŒŒì¼ ì—…ë¡œë“œ API í˜¸ì¶œ
    // const formData = new FormData();
    // formData.append('file', selectedFile);
    // const response = await fetch('/api/upload-properties', {
    //   method: 'POST',
    //   body: formData
    // });
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  // ì£¼ì†Œê²€ìƒ‰ìœ¼ë¡œ ì¢Œí‘œë³€í™˜ í•¨ìˆ˜
