import React, { useState } from 'react';
import styled from 'styled-components';
import GoogleMap from '../components/GoogleMap';
import { Property } from '../types';

const HomeContainer = styled.div`
  display: flex;
  height: calc(100vh - 120px);
  min-height: 500px;
  overflow: hidden;
`;

const MapSection = styled.div`
  flex: 1;
  position: relative;
  min-width: 0;
`;

const Sidebar = styled.div<{ isOpen: boolean }>`
  width: ${props => props.isOpen ? '400px' : '0px'};
  background: white;
  border-left: 1px solid #e5e7eb;
  overflow-y: auto;
  padding: ${props => props.isOpen ? '1rem' : '0'};
  flex-shrink: 0;
  transition: all 0.3s ease;
  position: relative;
`;

const ToggleButton = styled.button<{ isOpen: boolean }>`
  position: absolute;
  left: ${props => props.isOpen ? '-40px' : '-40px'};
  top: 50%;
  transform: translateY(-50%);
  width: 40px;
  height: 60px;
  background: #2563eb;
  color: white;
  border: none;
  border-radius: 8px 0 0 8px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.2rem;
  font-weight: bold;
  transition: all 0.3s ease;
  z-index: 100;

  &:hover {
    background: #1d4ed8;
  }
`;

const SearchSection = styled.div`
  padding: 1rem;
  border-bottom: 1px solid #e5e7eb;
  margin-bottom: 1rem;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 1rem;

  &:focus {
    outline: none;
    border-color: #2563eb;
    box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
  }
`;

const FilterSection = styled.div`
  padding: 1rem;
  border-bottom: 1px solid #e5e7eb;
  margin-bottom: 1rem;
`;

const FilterTitle = styled.h3`
  margin-bottom: 0.5rem;
  font-size: 1rem;
  font-weight: 600;
`;

const FilterGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 0.5rem;
`;

const FilterSelect = styled.select`
  padding: 0.5rem;
  border: 1px solid #d1d5db;
  border-radius: 4px;
  font-size: 0.875rem;
`;

const PropertyCard = styled.div`
  padding: 1rem;
  border: 1px solid #e5e7eb;
  border-radius: 8px;
  margin-bottom: 1rem;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    border-color: #2563eb;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  }
`;

const PropertyTitle = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const PropertyPrice = styled.div`
  font-size: 1.25rem;
  font-weight: bold;
  color: #2563eb;
  margin-bottom: 0.5rem;
`;

const PropertyDetails = styled.div`
  font-size: 0.875rem;
  color: #666;
  margin-bottom: 0.5rem;
`;

const PropertyAddress = styled.div`
  font-size: 0.875rem;
  color: #999;
`;

const SidebarTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: bold;
  color: #1f2937;
  margin-bottom: 1rem;
  text-align: center;
`;

const PropertyDetailModal = styled.div<{ isOpen: boolean }>`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: ${props => props.isOpen ? 'flex' : 'none'};
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 8px;
  padding: 2rem;
  width: 90%;
  max-width: 600px;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #666;
  
  &:hover {
    color: #333;
  }
`;

const ModalTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: bold;
  color: #1f2937;
  margin-bottom: 1rem;
`;

const ModalPrice = styled.div`
  font-size: 1.25rem;
  font-weight: bold;
  color: #2563eb;
  margin-bottom: 1rem;
`;

const ModalAddress = styled.div`
  font-size: 1rem;
  color: #666;
  margin-bottom: 1rem;
`;

const ModalDescription = styled.p`
  font-size: 1rem;
  line-height: 1.6;
  color: #374151;
  margin-bottom: 1rem;
`;

const ModalInfo = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
  margin-bottom: 1rem;
`;

const InfoItem = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
`;

const InfoLabel = styled.span`
  font-size: 0.875rem;
  color: #6b7280;
  font-weight: 500;
`;

const InfoValue = styled.span`
  font-size: 1rem;
  color: #1f2937;
  font-weight: 500;
`;

const ContactInfo = styled.div`
  margin-top: 1.5rem;
  padding-top: 1.5rem;
  border-top: 1px solid #e5e7eb;
`;

const ContactTitle = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.75rem;
`;

const ContactItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 1rem;
  color: #1f2937;
  margin-bottom: 0.5rem;
`;

// ÏÉòÌîå Îç∞Ïù¥ÌÑ∞
const sampleProperties: Property[] = [
  {
    id: '1',
    title: 'Í∞ïÎÇ®Íµ¨ Ïã†Ï∂ï ÏïÑÌååÌä∏',
    description: 'Í∞ïÎÇ®Íµ¨ Ïó≠ÏÇºÎèô Ïã†Ï∂ï ÏïÑÌååÌä∏ÏûÖÎãàÎã§. Ïó≠ÏÑ∏Í∂åÏúºÎ°ú ÍµêÌÜµÏù¥ Ìé∏Î¶¨Ìï©ÎãàÎã§.',
    price: 850000000,
    type: 'sale',
    propertyType: 'apartment',
    address: 'ÏÑúÏö∏Ïãú Í∞ïÎÇ®Íµ¨ Ïó≠ÏÇºÎèô 123-45',
    location: { lat: 37.5013, lng: 127.0396 },
    bedrooms: 3,
    bathrooms: 2,
    area: 84.5,
    images: ['https://via.placeholder.com/300x200'],
    contact: {
      name: 'ÍπÄÎ∂ÄÎèôÏÇ∞',
      phone: '02-1234-5678',
      email: 'kim@realestate.com'
    },
    features: ['Ïó≠ÏÑ∏Í∂å', 'Ïã†Ï∂ï', 'Ï£ºÏ∞®Í∞ÄÎä•'],
    createdAt: new Date(),
    isActive: true
  },
  {
    id: '2',
    title: 'ÌôçÎåÄÏûÖÍµ¨ Í∑ºÏ≤ò ÏõêÎ£∏',
    description: 'ÌôçÎåÄÏûÖÍµ¨Ïó≠ ÎèÑÎ≥¥ 5Î∂Ñ Í±∞Î¶¨Ïùò ÏõêÎ£∏ÏûÖÎãàÎã§.',
    price: 500000,
    type: 'rent',
    propertyType: 'apartment',
    address: 'ÏÑúÏö∏Ïãú ÎßàÌè¨Íµ¨ ÏÑúÍµêÎèô 456-78',
    location: { lat: 37.5572, lng: 126.9254 },
    bedrooms: 1,
    bathrooms: 1,
    area: 25.3,
    images: ['https://via.placeholder.com/300x200'],
    contact: {
      name: 'Î∞ïÎ∂ÄÎèôÏÇ∞',
      phone: '02-9876-5432',
      email: 'park@realestate.com'
    },
    features: ['Ïó≠ÏÑ∏Í∂å', 'ÏõêÎ£∏', 'Ï£ºÏ∞®Î∂àÍ∞Ä'],
    createdAt: new Date(),
    isActive: true
  }
];

const HomePage: React.FC = () => {
  const [properties] = useState<Property[]>(sampleProperties);
  const [selectedProperty, setSelectedProperty] = useState<Property | null>(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [filteredProperties, setFilteredProperties] = useState<Property[]>(sampleProperties);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalProperty, setModalProperty] = useState<Property | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    type: '',
    propertyType: '',
    minPrice: '',
    maxPrice: ''
  });

  const handleMarkerClick = (property: Property) => {
    setSelectedProperty(property);
    setFilteredProperties([property]); // Ìï¥Îãπ Îß§Î¨ºÎßå Î≥¥Ïù¥ÎèÑÎ°ù ÌïÑÌÑ∞ÎßÅ
    setIsSidebarOpen(true); // ÏÇ¨Ïù¥ÎìúÎ∞î Ïó¥Í∏∞
  };

  const handlePropertyCardClick = (property: Property) => {
    setModalProperty(property);
    setIsModalOpen(true);
  };

  const handleShowAllProperties = () => {
    setSelectedProperty(null);
    setFilteredProperties(properties); // Î™®Îì† Îß§Î¨º Î≥¥Ïù¥Í∏∞
  };

  const formatPrice = (price: number) => {
    if (price >= 100000000) {
      return `${Math.floor(price / 100000000)}ÏñµÏõê`;
    } else if (price >= 10000) {
      return `${Math.floor(price / 10000)}ÎßåÏõê`;
    }
    return `${price.toLocaleString()}Ïõê`;
  };

  // ÌïÑÌÑ∞ÎßÅ Î°úÏßÅ
  const getFilteredProperties = () => {
    let filtered = selectedProperty ? [selectedProperty] : properties;

    // Í≤ÄÏÉâÏñ¥ ÌïÑÌÑ∞ÎßÅ
    if (searchTerm) {
      filtered = filtered.filter(property => 
        property.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        property.address.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // ÌïÑÌÑ∞ Ï°∞Í±¥ Ï†ÅÏö©
    if (filters.type) {
      filtered = filtered.filter(property => property.type === filters.type);
    }
    if (filters.propertyType) {
      filtered = filtered.filter(property => property.propertyType === filters.propertyType);
    }
    if (filters.minPrice) {
      filtered = filtered.filter(property => property.price >= parseInt(filters.minPrice));
    }
    if (filters.maxPrice) {
      filtered = filtered.filter(property => property.price <= parseInt(filters.maxPrice));
    }

    return filtered;
  };

  const displayProperties = getFilteredProperties();

  return (
    <>
      <HomeContainer>
        <MapSection>
          <GoogleMap
            properties={properties}
            onMarkerClick={handleMarkerClick}
          />
        </MapSection>
        <Sidebar isOpen={isSidebarOpen}>
          <ToggleButton 
            isOpen={isSidebarOpen}
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          >
            {isSidebarOpen ? '‚óÄ' : '‚ñ∂'}
          </ToggleButton>
          {isSidebarOpen && (
            <>
              <SidebarTitle>
                {selectedProperty ? 'ÏÑ†ÌÉùÎêú Îß§Î¨º' : 'Îß§Î¨º Î™©Î°ù'}
                {selectedProperty && (
                  <button 
                    onClick={handleShowAllProperties}
                    style={{
                      marginLeft: '1rem',
                      padding: '0.25rem 0.5rem',
                      fontSize: '0.75rem',
                      background: '#2563eb',
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                  >
                    Ï†ÑÏ≤¥Î≥¥Í∏∞
                  </button>
                )}
              </SidebarTitle>

              <SearchSection>
                <SearchInput
                  type="text"
                  placeholder="ÏßÄÏó≠Î™Ö, Ïó≠Î™Ö, Îß§Î¨ºÎ™ÖÏúºÎ°ú Í≤ÄÏÉâ..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </SearchSection>

              <FilterSection>
                <FilterTitle>ÌïÑÌÑ∞</FilterTitle>
                <FilterGrid>
                  <FilterSelect
                    value={filters.type}
                    onChange={(e) => setFilters({ ...filters, type: e.target.value })}
                  >
                    <option value="">Í±∞Îûò Ïú†Ìòï</option>
                    <option value="sale">Îß§Îß§</option>
                    <option value="rent">ÏûÑÎåÄ</option>
                  </FilterSelect>
                  <FilterSelect
                    value={filters.propertyType}
                    onChange={(e) => setFilters({ ...filters, propertyType: e.target.value })}
                  >
                    <option value="">Îß§Î¨º Ïú†Ìòï</option>
                    <option value="apartment">ÏïÑÌååÌä∏</option>
                    <option value="house">Îã®ÎèÖÏ£ºÌÉù</option>
                    <option value="commercial">ÏÉÅÍ∞Ä</option>
                    <option value="land">ÌÜ†ÏßÄ</option>
                  </FilterSelect>
                </FilterGrid>
              </FilterSection>

              {displayProperties.map(property => (
                <PropertyCard
                  key={property.id}
                  onClick={() => handlePropertyCardClick(property)}
                >
                  <PropertyTitle>{property.title}</PropertyTitle>
                  <PropertyPrice>{formatPrice(property.price)}</PropertyPrice>
                  <PropertyDetails>
                    {property.bedrooms && `${property.bedrooms}Ïπ®Ïã§`}
                    {property.bathrooms && ` ${property.bathrooms}ÏöïÏã§`}
                    {property.area && ` ${property.area}„é°`}
                  </PropertyDetails>
                  <PropertyAddress>{property.address}</PropertyAddress>
                </PropertyCard>
              ))}
            </>
          )}
        </Sidebar>
      </HomeContainer>
      
      <PropertyDetailModal isOpen={isModalOpen} onClick={() => setIsModalOpen(false)}>
        <ModalContent onClick={(e) => e.stopPropagation()}>
          <CloseButton onClick={() => setIsModalOpen(false)}>&times;</CloseButton>
          {modalProperty && (
            <>
              <ModalTitle>{modalProperty.title}</ModalTitle>
              <ModalPrice>{formatPrice(modalProperty.price)}</ModalPrice>
              <ModalAddress>{modalProperty.address}</ModalAddress>
              <ModalDescription>{modalProperty.description}</ModalDescription>
              
              <ModalInfo>
                <InfoItem>
                  <InfoLabel>Í±∞Îûò Ïú†Ìòï</InfoLabel>
                  <InfoValue>{modalProperty.type === 'sale' ? 'Îß§Îß§' : 'ÏûÑÎåÄ'}</InfoValue>
                </InfoItem>
                <InfoItem>
                  <InfoLabel>Îß§Î¨º Ïú†Ìòï</InfoLabel>
                  <InfoValue>
                    {modalProperty.propertyType === 'apartment' ? 'ÏïÑÌååÌä∏' :
                     modalProperty.propertyType === 'house' ? 'Îã®ÎèÖÏ£ºÌÉù' :
                     modalProperty.propertyType === 'commercial' ? 'ÏÉÅÍ∞Ä' : 'ÌÜ†ÏßÄ'}
                  </InfoValue>
                </InfoItem>
                {modalProperty.bedrooms && (
                  <InfoItem>
                    <InfoLabel>Ïπ®Ïã§</InfoLabel>
                    <InfoValue>{modalProperty.bedrooms}Í∞ú</InfoValue>
                  </InfoItem>
                )}
                {modalProperty.bathrooms && (
                  <InfoItem>
                    <InfoLabel>ÏöïÏã§</InfoLabel>
                    <InfoValue>{modalProperty.bathrooms}Í∞ú</InfoValue>
                  </InfoItem>
                )}
                {modalProperty.area && (
                  <InfoItem>
                    <InfoLabel>Î©¥Ï†Å</InfoLabel>
                    <InfoValue>{modalProperty.area}„é°</InfoValue>
                  </InfoItem>
                )}
              </ModalInfo>

              <ContactInfo>
                <ContactTitle>Ïó∞ÎùΩÏ≤ò</ContactTitle>
                <ContactItem>
                  <span>üìû</span>
                  <span>{modalProperty.contact.name}</span>
                </ContactItem>
                <ContactItem>
                  <span>üì±</span>
                  <span>{modalProperty.contact.phone}</span>
                </ContactItem>
                <ContactItem>
                  <span>‚úâÔ∏è</span>
                  <span>{modalProperty.contact.email}</span>
                </ContactItem>
              </ContactInfo>
            </>
          )}
        </ModalContent>
      </PropertyDetailModal>
    </>
  );
};

export default HomePage;
