interface HomePageProps {
  searchTerm?: string;
  addressSearch?: string;
  filters?: {
    type: string;
    propertyType: string;
    area: string;
    price: string;
    deposit: string;
  };
  onPropertyAdded?: (properties: Property[]) => void;
}

const HomePage: React.FC<HomePageProps> = ({ 
  searchTerm = '', 
  addressSearch = '',
  filters = { type: '', propertyType: '', area: '', price: '', deposit: '' },
  onPropertyAdded
}) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [properties] = useState<Property[]>(sampleProperties);
  const [selectedClusterProperties, setSelectedClusterProperties] = useState<Property[]>([]);
  const [selectedMarkerProperties, setSelectedMarkerProperties] = useState<Property[]>([]);
  const [selectedMarkerId, setSelectedMarkerId] = useState<string | null>(null);
  const [selectedClusterId, setSelectedClusterId] = useState<string | null>(null);
  const [selectedPropertyForDetail, setSelectedPropertyForDetail] = useState<Property | null>(null);

  const handleMarkerClick = (property: Property) => {
    console.log(`마커 클릭: ${property.title} (${property.id})`);
    setSelectedClusterProperties([]); 
    setSelectedClusterId(''); 
    setSelectedMarkerProperties([property]); 
    setSelectedMarkerId(property.id); 
    setIsSidebarOpen(true);
  };

  const handleClusterClick = (clusterProperties: Property[]) => {
    console.log(`클러스터 클릭: ${clusterProperties.length}개 매물 선택`);
    setSelectedMarkerProperties([]); 
    setSelectedMarkerId(null); 
    setSelectedClusterProperties(clusterProperties);
    setSelectedClusterId(clusterProperties.map(p => p.id).join(',')); 
    setIsSidebarOpen(true);
  };

  const handleShowAllProperties = () => {
    setSelectedClusterProperties([]);
    setSelectedMarkerProperties([]);
    setSelectedMarkerId(null);
    setSelectedClusterId('');
  };

  const handlePropertyCardClick = (property: Property) => {
    console.log('매물 카드 클릭:', property.title);
    setSelectedPropertyForDetail(property);
  };

  const handleCloseDetailModal = () => {
    setSelectedPropertyForDetail(null);
  };

  const formatPrice = (price: number) => {
    if (price >= 100000000) {
      return `${Math.floor(price / 100000000)}억원`;
    } else if (price >= 10000) {
      return `${Math.floor(price / 10000)}만원`;
    }
    return `${price.toLocaleString()}원`;
  };

  const cleanPropertyTitle = (title: string) => {
    return title.replace(/숙/g, '').trim();
  };

  const maskAddress = (address: string) => {
    if (!address) return '';
    const parts = address.split(' ');
    const maskedParts = parts.slice(0, 3);
    return maskedParts.join(' ');
  };

  const getFilteredProperties = () => {
    let filtered = properties;

    if (searchTerm) {
      filtered = filtered.filter(property => 
        property.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        property.id.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (addressSearch) {
      filtered = filtered.filter(property => 
        property.address.toLowerCase().includes(addressSearch.toLowerCase())
      );
    }

    if (filters.type) {
      filtered = filtered.filter(property => property.type === filters.type);
    }
    if (filters.propertyType) {
      filtered = filtered.filter(property => property.propertyType === filters.propertyType);
    }

    return filtered;
  };

  const displayProperties = selectedClusterProperties.length > 0
    ? selectedClusterProperties
    : selectedMarkerProperties.length > 0
      ? selectedMarkerProperties
      : getFilteredProperties();
  const filteredProperties = getFilteredProperties();

  return (
    <HomeContainer>
      <MapSection>
        <GoogleMap
          properties={filteredProperties}
          onMarkerClick={handleMarkerClick}
          onClusterClick={handleClusterClick}
          selectedMarkerId={selectedMarkerId}
          setSelectedMarkerId={setSelectedMarkerId}
          selectedClusterId={selectedClusterId}
          setSelectedClusterId={setSelectedClusterId}
        />
      </MapSection>
      <Sidebar isOpen={isSidebarOpen}>
        <ToggleButton 
          isOpen={isSidebarOpen}
          onClick={() => setIsSidebarOpen(!isSidebarOpen)}
        >
          {isSidebarOpen ? '◀' : '▶'}
        </ToggleButton>
        {isSidebarOpen && (
          <>
            <SidebarTitle>
              {selectedClusterProperties.length > 0 
                ? `선택된 클러스터 매물 (${selectedClusterProperties.length}개)`
                : selectedMarkerProperties.length > 0
                  ? `선택된 마커 매물 (${selectedMarkerProperties.length}개)`
                  : '매물 목록'}
              {(selectedClusterProperties.length > 0 || selectedMarkerProperties.length > 0) && (
                <button 
                  onClick={handleShowAllProperties}
                  style={{
                    marginLeft: '1rem',
                    padding: '0.25rem 0.5rem',
                    fontSize: '0.75rem',
                    background: '#2563eb',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  전체보기
                </button>
              )}
            </SidebarTitle>

            {!selectedClusterProperties.length && !selectedMarkerProperties.length && (
              <FilterInfo>
                총 {properties.length}개 중 {filteredProperties.length}개 표시
                {(searchTerm || addressSearch || Object.values(filters).some(f => f)) && (
                  <div style={{ marginTop: '0.25rem', fontSize: '0.75rem', color: '#94a3b8' }}>
                    필터 적용됨
                  </div>
                )}
              </FilterInfo>
            )}

            {selectedClusterProperties.length > 0 && (
              <FilterInfo>
                <div style={{ color: '#dc2626', fontWeight: 'bold', fontSize: '1rem' }}>
                  {selectedClusterProperties.length}개 매물만 표시
                </div>
                <div style={{ marginTop: '0.25rem', fontSize: '0.75rem', color: '#94a3b8' }}>
                  클러스터에서 선택된 매물들
                </div>
              </FilterInfo>
            )}

            {selectedMarkerProperties.length > 0 && (
              <FilterInfo>
                <div style={{ color: '#dc2626', fontWeight: 'bold', fontSize: '1rem' }}>
                  {selectedMarkerProperties.length}개 매물만 표시
                </div>
                <div style={{ marginTop: '0.25rem', fontSize: '0.75rem', color: '#94a3b8' }}>
                  마커에서 선택된 매물들
                </div>
              </FilterInfo>
            )}

            {displayProperties.map(property => (
              <PropertyCard 
                key={property.id}
                onClick={() => handlePropertyCardClick(property)}
              >
                <PropertyCardImage>
                  <PropertyCardImageElement 
                    src={property.images[0]} 
                    alt={property.title}
                    onError={(e) => {
                      const target = e.target as HTMLImageElement;
                      target.src = 'data:image/svg+xml;base64,';
                    }}
                  />
                </PropertyCardImage>
                <PropertyCardContent>
                  <PropertyCardHeader>
                    <PropertyCardInfo>
                      <PropertyCardNumber>{property.id} {maskAddress(property.address)}</PropertyCardNumber>
                      <PropertyCardTitle>{cleanPropertyTitle(property.title)}</PropertyCardTitle>
                      <PropertyCardDetails>
                        전용 {Math.round(property.area / 3.3058)}평
                        {property.bedrooms && ` ${property.bedrooms}침실`}
                        {property.bathrooms && ` ${property.bathrooms}욕실`}
                      </PropertyCardDetails>
                      <PropertyCardPricing>
                        {property.type === 'rent' ? (
                          <>
                            <PriceBox type="deposit">
                              <PriceLabel>보증금</PriceLabel>
                              <PriceValue>{formatPrice(property.deposit || 0)}</PriceValue>
                            </PriceBox>
                            <PriceBox type="rent">
                              <PriceLabel>월세</PriceLabel>
                              <PriceValue>{formatPrice(property.price)}</PriceValue>
                            </PriceBox>
                          </>
                        ) : (
                          <PriceBox type="sale">
                            <PriceLabel>매매</PriceLabel>
                            <PriceValue>{formatPrice(property.price)}</PriceValue>
                          </PriceBox>
                        )}
                      </PropertyCardPricing>
                    </PropertyCardInfo>
                    <PropertyCardActions>
                      <EditButton 
                        onClick={(e) => {
                          e.stopPropagation();
                          console.log('수정 버튼 클릭:', property.title);
                        }}
                      >
                        수정
                      </EditButton>
                      <DeleteButton 
                        onClick={(e) => {
                          e.stopPropagation();
                          console.log('삭제 버튼 클릭:', property.title);
                        }}
                      >
                        삭제
                      </DeleteButton>
                    </PropertyCardActions>
                  </PropertyCardHeader>
                </PropertyCardContent>
              </PropertyCard>
            ))}
          </>
        )}
      </Sidebar>
      
      {selectedPropertyForDetail && (
        <PropertyDetailModal
          property={selectedPropertyForDetail}
          onClose={handleCloseDetailModal}
        />
      )}
    </HomeContainer>
  );
};

export default HomePage;
