import React from 'react';
import styled from 'styled-components';
import { Property } from '../types';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 1rem;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 8px;
  width: 95%;
  max-width: 1400px;
  height: 90vh;
  display: flex;
  overflow: hidden;
  position: relative;
`;

const LeftPanel = styled.div`
  width: 60%;
  padding: 2rem;
  overflow-y: auto;
  border-right: 1px solid #e5e7eb;
`;

const RightPanel = styled.div`
  width: 40%;
  background: #f8fafc;
  position: relative;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #666;
  z-index: 10;

  &:hover {
    color: #333;
  }
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 2rem;
`;

const PropertyTitle = styled.h1`
  font-size: 1.5rem;
  font-weight: bold;
  color: #1f2937;
  margin: 0;
`;

const PropertyNumber = styled.div`
  font-size: 0.875rem;
  color: #6b7280;
  margin-bottom: 0.5rem;
`;

const ConfirmedDate = styled.div`
  font-size: 0.875rem;
  color: #059669;
  font-weight: 600;
`;

const ImageSection = styled.div`
  margin-bottom: 2rem;
  position: relative;
`;

const MainImage = styled.img`
  width: 100%;
  height: 400px;
  object-fit: cover;
  border-radius: 8px;
  margin-bottom: 1rem;
`;

const ImageInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const ImageInfoBox = styled.div`
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 4px;
  padding: 0.75rem;
  font-size: 0.875rem;
  color: #374151;
`;

const ImageCounter = styled.div`
  position: absolute;
  bottom: 1rem;
  right: 1rem;
  background: rgba(0, 0, 0, 0.7);
  color: white;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  font-size: 0.75rem;
`;

const Section = styled.div`
  margin-bottom: 2rem;
`;

const SectionTitle = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 1rem;
  border-bottom: 2px solid #e5e7eb;
  padding-bottom: 0.5rem;
`;

const InfoGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: 0.5rem;
`;

const InfoItem = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0.5rem 0;
  border-bottom: 1px solid #f3f4f6;
`;

const InfoLabel = styled.span`
  font-size: 0.875rem;
  color: #6b7280;
  font-weight: 500;
  min-width: 120px;
`;

const InfoValue = styled.span`
  font-size: 0.875rem;
  color: #1f2937;
  font-weight: 500;
  text-align: right;
  flex: 1;
`;

const DescriptionBox = styled.div`
  background: #f8fafc;
  border: 1px solid #e5e7eb;
  border-radius: 6px;
  padding: 1rem;
  margin-bottom: 1rem;
`;

const TemplateLink = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: #dc2626;
  font-size: 0.875rem;
  font-weight: 500;
  margin-bottom: 1rem;
`;

const ContactInfo = styled.div`
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 6px;
  padding: 1rem;
`;

const ContactItem = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0.25rem 0;
  font-size: 0.875rem;
`;

const MapPlaceholder = styled.div`
  width: 100%;
  height: 100%;
  background: #e5e7eb;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #6b7280;
  font-size: 1rem;
`;

const formatPrice = (price: number) => {
  if (price >= 100000000) {
    return `${Math.floor(price / 100000000)}억원`;
  } else if (price >= 10000) {
    return `${Math.floor(price / 10000)}만원`;
  }
  return `${price.toLocaleString()}원`;
};

const maskAddress = (address: string) => {
  if (!address) return '';
  const parts = address.split(' ');
  const maskedParts = parts.slice(0, 3);
  return maskedParts.join(' ');
};

interface PropertyDetailModalProps {
  property: Property;
  onClose: () => void;
}

const PropertyDetailModal: React.FC<PropertyDetailModalProps> = ({ property, onClose }) => {
  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <CloseButton onClick={onClose}>&times;</CloseButton>

        <LeftPanel>
          <Header>
            <div>
              <PropertyNumber>매물번호 {property.id}</PropertyNumber>
              <PropertyTitle>{property.title}</PropertyTitle>
            </div>
            {property.confirmedDate && (
              <ConfirmedDate>확인매물 {property.confirmedDate}</ConfirmedDate>
            )}
          </Header>

          <ImageSection>
            {property.images && property.images.length > 0 && (
              <MainImage src={property.images[0]} alt={property.title} />
            )}
            <ImageInfo>
              <ImageInfoBox>1. 사진직접입력 버튼설치</ImageInfoBox>
              <ImageInfoBox>2. 첫번째 입력사진이 대표사진</ImageInfoBox>
            </ImageInfo>
            <ImageCounter>1/8</ImageCounter>
          </ImageSection>

          <Section>
            <SectionTitle>매물정보</SectionTitle>
            <InfoGrid>
              <InfoItem>
                <InfoLabel>주소</InfoLabel>
                <InfoValue>{maskAddress(property.address)}</InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>매물종류</InfoLabel>
                <InfoValue>
                  {property.propertyType === 'apartment' ? '아파트' :
                   property.propertyType === 'house' ? '단독주택' :
                   property.propertyType === 'commercial' ? '상가' : '토지'}
                </InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>거래유형</InfoLabel>
                <InfoValue>{property.type === 'sale' ? '매매' : '임대'}</InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>매매가</InfoLabel>
                <InfoValue>{property.type === 'sale' ? formatPrice(property.price) : '-'}</InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>보증금/임대료</InfoLabel>
                <InfoValue>
                  {property.type === 'rent' 
                    ? `보증금 ${formatPrice(property.deposit || 0)} / 월세 ${formatPrice(property.price)}`
                    : '-'
                  }
                </InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>관리비</InfoLabel>
                <InfoValue>-</InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>융자금</InfoLabel>
                <InfoValue>-</InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>공급/전용면적</InfoLabel>
                <InfoValue>
                  {Math.round(property.area / 3.3058)}평 ({property.area}㎡)
                </InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>건축물용도</InfoLabel>
                <InfoValue>
                  {property.propertyType === 'apartment' ? '주거용' :
                   property.propertyType === 'commercial' ? '상업용' : '주거용'}
                </InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>해당층/전체층</InfoLabel>
                <InfoValue>{property.floor || '-'}</InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>방/화장실</InfoLabel>
                <InfoValue>
                  {property.bedrooms || 0}방 / {property.bathrooms || 0}화장실
                </InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>난방유형</InfoLabel>
                <InfoValue>-</InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>주차</InfoLabel>
                <InfoValue>{property.parking ? '가능' : '불가능'}</InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>엘리베이터</InfoLabel>
                <InfoValue>{property.elevator ? '있음' : '없음'}</InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>사용승인일</InfoLabel>
                <InfoValue>-</InfoValue>
              </InfoItem>
              <InfoItem>
                <InfoLabel>입주가능일</InfoLabel>
                <InfoValue>-</InfoValue>
              </InfoItem>
            </InfoGrid>
          </Section>

          <Section>
            <SectionTitle>매물설명</SectionTitle>
            <TemplateLink>
              <span>●</span>
              <span>매물등록 템플릿과 연동</span>
              <span>↓</span>
            </TemplateLink>
            <ContactInfo>
              <ContactItem>
                <span>중개업소</span>
                <span>{property.contact.name}</span>
              </ContactItem>
              <ContactItem>
                <span>전화번호</span>
                <span>{property.contact.phone}</span>
              </ContactItem>
              <ContactItem>
                <span>이메일</span>
                <span>{property.contact.email}</span>
              </ContactItem>
              <ContactItem>
                <span>주소</span>
                <span>{property.contact.name} 사무실</span>
              </ContactItem>
              <ContactItem>
                <span>등록번호</span>
                <span>-</span>
              </ContactItem>
            </ContactInfo>
          </Section>

          <Section>
            <SectionTitle>위치정보</SectionTitle>
            <MapPlaceholder>
              지도 영역 (위치 정보 표시)
            </MapPlaceholder>
          </Section>
        </LeftPanel>

        <RightPanel>
          <MapPlaceholder>
            지도 영역 (Google Maps 연동)
          </MapPlaceholder>
        </RightPanel>
      </ModalContent>
    </ModalOverlay>
  );
};

export default PropertyDetailModal;
