interface HomePageProps {
  searchTerm?: string;
  addressSearch?: string;
  filters?: {
    type: string;
    propertyType: string;
    area: string;
    price: string;
    deposit: string;
  };
  onPropertyAdded?: (properties: Property[]) => void;
}

const HomePage: React.FC<HomePageProps> = ({ 
  searchTerm = '', 
  addressSearch = '',
  filters = { type: '', propertyType: '', area: '', price: '', deposit: '' },
  onPropertyAdded
}) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [properties, setProperties] = useState<Property[]>(sampleProperties);
  const [selectedClusterProperties, setSelectedClusterProperties] = useState<Property[]>([]);
  const [selectedMarkerProperties, setSelectedMarkerProperties] = useState<Property[]>([]);
  const [selectedMarkerId, setSelectedMarkerId] = useState<string | null>(null);
  const [selectedClusterId, setSelectedClusterId] = useState<string | null>(null);
  const [selectedPropertyForDetail, setSelectedPropertyForDetail] = useState<Property | null>(null);
  const [isAdmin] = useState(true); // 개발 환경에서는 true로 설정, 배포 시 false로 변경
  const [editingProperty, setEditingProperty] = useState<Property | null>(null);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState<string | null>(null);

  const handleMarkerClick = (property: Property) => {
    console.log(`마커 클릭: ${property.title} (${property.id})`);
    setSelectedClusterProperties([]); 
    setSelectedClusterId(''); 
    setSelectedMarkerProperties([property]); 
    setSelectedMarkerId(property.id); 
    setIsSidebarOpen(true);
  };

  const handleClusterClick = (clusterProperties: Property[]) => {
    console.log(`클러스터 클릭: ${clusterProperties.length}개 매물 선택`);
    setSelectedMarkerProperties([]); 
    setSelectedMarkerId(null); 
    setSelectedClusterProperties(clusterProperties);
    setSelectedClusterId(clusterProperties.map(p => p.id).join(',')); 
    setIsSidebarOpen(true);
  };

  const handleShowAllProperties = () => {
    setSelectedClusterProperties([]);
    setSelectedMarkerProperties([]);
    setSelectedMarkerId(null);
    setSelectedClusterId('');
  };

  const handlePropertyCardClick = (property: Property) => {
    console.log('매물 카드 클릭:', property.title);
    setSelectedPropertyForDetail(property);
  };

  const handleCloseDetailModal = () => {
    setSelectedPropertyForDetail(null);
  };

  const formatPrice = (price: number) => {
    if (price >= 100000000) {
      return `${Math.floor(price / 100000000)}억원`;
    } else if (price >= 10000) {
      return `${Math.floor(price / 10000)}만원`;
    }
    return `${price.toLocaleString()}원`;
  };

  const cleanPropertyTitle = (title: string) => {
    return title.replace(/숙/g, '').trim();
  };

  const maskAddress = (address: string) => {
    if (!address) return '';
    const parts = address.split(' ');
    const maskedParts = parts.slice(0, 3);
    return maskedParts.join(' ');
  };

  const getFilteredProperties = () => {
    let filtered = properties;

    if (searchTerm) {
      filtered = filtered.filter(property => 
        property.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        property.id.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (addressSearch) {
      filtered = filtered.filter(property => 
        property.address.toLowerCase().includes(addressSearch.toLowerCase())
      );
    }

    if (filters.type) {
      filtered = filtered.filter(property => property.type === filters.type);
    }
    if (filters.propertyType) {
      filtered = filtered.filter(property => property.propertyType === filters.propertyType);
    }

    return filtered;
  };

  const displayProperties = selectedClusterProperties.length > 0
    ? selectedClusterProperties
    : selectedMarkerProperties.length > 0
      ? selectedMarkerProperties
      : getFilteredProperties();
  const filteredProperties = getFilteredProperties();

  const handleEditProperty = (property: Property) => {
    console.log('수정 버튼 클릭:', property.title);
    setEditingProperty(property);
  };

  const handleDeleteProperty = (property: Property) => {
    console.log('삭제 버튼 클릭:', property.title);
    setShowDeleteConfirm(property.id);
  };

  const handleConfirmDelete = () => {
    if (showDeleteConfirm) {
      setProperties(prev => prev.filter(p => p.id !== showDeleteConfirm));
      setShowDeleteConfirm(null);
      console.log('매물 삭제 완료:', showDeleteConfirm);
    }
  };

  const handleCancelDelete = () => {
    setShowDeleteConfirm(null);
  };

  const handleSaveEdit = (updatedProperty: Property) => {
    setProperties(prev => prev.map(p => p.id === updatedProperty.id ? updatedProperty : p));
    setEditingProperty(null);
    console.log('매물 수정 완료:', updatedProperty.title);
  };

  const handleCancelEdit = () => {
    setEditingProperty(null);
  };

  return (
    <HomeContainer>
      <MapSection>
        <GoogleMap
          properties={filteredProperties}
          onMarkerClick={handleMarkerClick}
          onClusterClick={handleClusterClick}
          selectedMarkerId={selectedMarkerId}
          setSelectedMarkerId={setSelectedMarkerId}
          selectedClusterId={selectedClusterId}
          setSelectedClusterId={setSelectedClusterId}
        />
      </MapSection>
      <Sidebar isOpen={isSidebarOpen}>
        <ToggleButton 
          isOpen={isSidebarOpen}
          onClick={() => setIsSidebarOpen(!isSidebarOpen)}
        >
          {isSidebarOpen ? '◀' : '▶'}
        </ToggleButton>
        {isSidebarOpen && (
          <>
            <SidebarTitle>
              {selectedClusterProperties.length > 0 
                ? `선택된 클러스터 매물 (${selectedClusterProperties.length}개)`
                : selectedMarkerProperties.length > 0
                  ? `선택된 마커 매물 (${selectedMarkerProperties.length}개)`
                  : '매물 목록'}
              {(selectedClusterProperties.length > 0 || selectedMarkerProperties.length > 0) && (
                <button 
                  onClick={handleShowAllProperties}
                  style={{
                    marginLeft: '1rem',
                    padding: '0.25rem 0.5rem',
                    fontSize: '0.75rem',
                    background: '#2563eb',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  전체보기
                </button>
              )}
            </SidebarTitle>

            {!selectedClusterProperties.length && !selectedMarkerProperties.length && (
              <FilterInfo>
                총 {properties.length}개 중 {filteredProperties.length}개 표시
                {(searchTerm || addressSearch || Object.values(filters).some(f => f)) && (
                  <div style={{ marginTop: '0.25rem', fontSize: '0.75rem', color: '#94a3b8' }}>
                    필터 적용됨
                  </div>
                )}
              </FilterInfo>
            )}

            {selectedClusterProperties.length > 0 && (
              <FilterInfo>
                <div style={{ color: '#dc2626', fontWeight: 'bold', fontSize: '1rem' }}>
                  {selectedClusterProperties.length}개 매물만 표시
                </div>
                <div style={{ marginTop: '0.25rem', fontSize: '0.75rem', color: '#94a3b8' }}>
                  클러스터에서 선택된 매물들
                </div>
              </FilterInfo>
            )}

            {selectedMarkerProperties.length > 0 && (
              <FilterInfo>
                <div style={{ color: '#dc2626', fontWeight: 'bold', fontSize: '1rem' }}>
                  {selectedMarkerProperties.length}개 매물만 표시
                </div>
                <div style={{ marginTop: '0.25rem', fontSize: '0.75rem', color: '#94a3b8' }}>
                  마커에서 선택된 매물들
                </div>
              </FilterInfo>
            )}

            {displayProperties.map(property => (
              <PropertyCard 
                key={property.id}
                onClick={() => handlePropertyCardClick(property)}
              >
                <PropertyCardImage>
                  <PropertyCardImageElement 
                    src={property.images[0]} 
                    alt={property.title}
                    onError={(e) => {
                      const target = e.target as HTMLImageElement;
                      target.src = 'data:image/svg+xml;base64,';
                    }}
                  />
                </PropertyCardImage>
                <PropertyCardContent>
                  <PropertyCardHeader>
                    <PropertyCardInfo>
                      <PropertyCardNumber>{property.id} {maskAddress(property.address)}</PropertyCardNumber>
                      <PropertyCardTitle>{cleanPropertyTitle(property.title)}</PropertyCardTitle>
                      <PropertyCardDetails>
                        전용 {Math.round(property.area / 3.3058)}평
                        {property.bedrooms && ` ${property.bedrooms}침실`}
                        {property.bathrooms && ` ${property.bathrooms}욕실`}
                      </PropertyCardDetails>
                      <PropertyCardPricing>
                        {property.type === 'rent' ? (
                          <>
                            <PriceBox type="deposit">
                              <PriceLabel>보증금</PriceLabel>
                              <PriceValue>{formatPrice(property.deposit || 0)}</PriceValue>
                            </PriceBox>
                            <PriceBox type="rent">
                              <PriceLabel>월세</PriceLabel>
                              <PriceValue>{formatPrice(property.price)}</PriceValue>
                            </PriceBox>
                          </>
                        ) : (
                          <PriceBox type="sale">
                            <PriceLabel>매매</PriceLabel>
                            <PriceValue>{formatPrice(property.price)}</PriceValue>
                          </PriceBox>
                        )}
                      </PropertyCardPricing>
                    </PropertyCardInfo>
                    <PropertyCardActions>
                      {isAdmin && (
                        <>
                          <EditButton 
                            onClick={(e) => {
                              e.stopPropagation();
                              handleEditProperty(property);
                            }}
                          >
                            수정
                          </EditButton>
                          <DeleteButton 
                            onClick={(e) => {
                              e.stopPropagation();
                              handleDeleteProperty(property);
                            }}
                          >
                            삭제
                          </DeleteButton>
                        </>
                      )}
                    </PropertyCardActions>
                  </PropertyCardHeader>
                </PropertyCardContent>
              </PropertyCard>
            ))}
          </>
        )}
      </Sidebar>
      
      {selectedPropertyForDetail && (
        <PropertyDetailModal
          property={selectedPropertyForDetail}
          onClose={handleCloseDetailModal}
        />
      )}

      {/* 삭제 확인 모달 */}
      {showDeleteConfirm && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div style={{
            background: 'white',
            padding: '2rem',
            borderRadius: '8px',
            maxWidth: '400px',
            width: '90%'
          }}>
            <h3 style={{ marginBottom: '1rem', color: '#dc2626' }}>매물 삭제</h3>
            <p style={{ marginBottom: '1.5rem' }}>
              정말로 이 매물을 삭제하시겠습니까?
            </p>
            <div style={{ display: 'flex', gap: '1rem', justifyContent: 'flex-end' }}>
              <button
                onClick={handleCancelDelete}
                style={{
                  padding: '0.5rem 1rem',
                  background: '#6b7280',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                취소
              </button>
              <button
                onClick={handleConfirmDelete}
                style={{
                  padding: '0.5rem 1rem',
                  background: '#dc2626',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                삭제
              </button>
            </div>
          </div>
        </div>
      )}

      {/* 수정 모달 */}
      {editingProperty && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div style={{
            background: 'white',
            padding: '2rem',
            borderRadius: '8px',
            maxWidth: '500px',
            width: '90%',
            maxHeight: '80vh',
            overflow: 'auto'
          }}>
            <h3 style={{ marginBottom: '1rem' }}>매물 수정</h3>
            <PropertyEditForm
              property={editingProperty}
              onSave={handleSaveEdit}
              onCancel={handleCancelEdit}
            />
          </div>
        </div>
      )}
    </HomeContainer>
  );
};

// 매물 수정 폼 컴포넌트
const PropertyEditForm: React.FC<{
  property: Property;
  onSave: (property: Property) => void;
  onCancel: () => void;
}> = ({ property, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    title: property.title,
    description: property.description,
    price: property.price,
    deposit: property.deposit || 0,
    type: property.type,
    propertyType: property.propertyType,
    address: property.address,
    bedrooms: property.bedrooms,
    bathrooms: property.bathrooms,
    area: property.area
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const updatedProperty = {
      ...property,
      ...formData
    };
    onSave(updatedProperty);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div style={{ marginBottom: '1rem' }}>
        <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
          매물명
        </label>
        <input
          type="text"
          value={formData.title}
          onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
          style={{
            width: '100%',
            padding: '0.5rem',
            border: '1px solid #d1d5db',
            borderRadius: '4px'
          }}
          required
        />
      </div>

      <div style={{ marginBottom: '1rem' }}>
        <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
          설명
        </label>
        <textarea
          value={formData.description}
          onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
          style={{
            width: '100%',
            padding: '0.5rem',
            border: '1px solid #d1d5db',
            borderRadius: '4px',
            minHeight: '80px'
          }}
          required
        />
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginBottom: '1rem' }}>
        <div>
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
            가격
          </label>
          <input
            type="number"
            value={formData.price}
            onChange={(e) => setFormData(prev => ({ ...prev, price: Number(e.target.value) }))}
            style={{
              width: '100%',
              padding: '0.5rem',
              border: '1px solid #d1d5db',
              borderRadius: '4px'
            }}
            required
          />
        </div>

        <div>
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
            보증금
          </label>
          <input
            type="number"
            value={formData.deposit}
            onChange={(e) => setFormData(prev => ({ ...prev, deposit: Number(e.target.value) }))}
            style={{
              width: '100%',
              padding: '0.5rem',
              border: '1px solid #d1d5db',
              borderRadius: '4px'
            }}
          />
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginBottom: '1rem' }}>
        <div>
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
            거래유형
          </label>
          <select
            value={formData.type}
            onChange={(e) => setFormData(prev => ({ ...prev, type: e.target.value as 'sale' | 'rent' }))}
            style={{
              width: '100%',
              padding: '0.5rem',
              border: '1px solid #d1d5db',
              borderRadius: '4px'
            }}
            required
          >
            <option value="sale">매매</option>
            <option value="rent">임대</option>
          </select>
        </div>

        <div>
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
            매물종류
          </label>
          <select
            value={formData.propertyType}
            onChange={(e) => setFormData(prev => ({ ...prev, propertyType: e.target.value as 'apartment' | 'house' | 'commercial' }))}
            style={{
              width: '100%',
              padding: '0.5rem',
              border: '1px solid #d1d5db',
              borderRadius: '4px'
            }}
            required
          >
            <option value="apartment">아파트</option>
            <option value="house">단독주택</option>
            <option value="commercial">상가</option>
          </select>
        </div>
      </div>

      <div style={{ marginBottom: '1rem' }}>
        <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
          주소
        </label>
        <input
          type="text"
          value={formData.address}
          onChange={(e) => setFormData(prev => ({ ...prev, address: e.target.value }))}
          style={{
            width: '100%',
            padding: '0.5rem',
            border: '1px solid #d1d5db',
            borderRadius: '4px'
          }}
          required
        />
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '1rem', marginBottom: '1rem' }}>
        <div>
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
            침실
          </label>
          <input
            type="number"
            value={formData.bedrooms}
            onChange={(e) => setFormData(prev => ({ ...prev, bedrooms: Number(e.target.value) }))}
            style={{
              width: '100%',
              padding: '0.5rem',
              border: '1px solid #d1d5db',
              borderRadius: '4px'
            }}
            min="0"
          />
        </div>

        <div>
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
            욕실
          </label>
          <input
            type="number"
            value={formData.bathrooms}
            onChange={(e) => setFormData(prev => ({ ...prev, bathrooms: Number(e.target.value) }))}
            style={{
              width: '100%',
              padding: '0.5rem',
              border: '1px solid #d1d5db',
              borderRadius: '4px'
            }}
            min="0"
          />
        </div>

        <div>
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
            면적 (m²)
          </label>
          <input
            type="number"
            value={formData.area}
            onChange={(e) => setFormData(prev => ({ ...prev, area: Number(e.target.value) }))}
            style={{
              width: '100%',
              padding: '0.5rem',
              border: '1px solid #d1d5db',
              borderRadius: '4px'
            }}
            step="0.1"
            required
          />
        </div>
      </div>

      <div style={{ display: 'flex', gap: '1rem', justifyContent: 'flex-end' }}>
        <button
          type="button"
          onClick={onCancel}
          style={{
            padding: '0.5rem 1rem',
            background: '#6b7280',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          취소
        </button>
        <button
          type="submit"
          style={{
            padding: '0.5rem 1rem',
            background: '#10b981',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          저장
        </button>
      </div>
    </form>
  );
};

export default HomePage;
