import React, { useState } from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 12px;
  padding: 2rem;
  width: 90%;
  max-width: 600px;
  max-height: 90vh;
  overflow-y: auto;
  box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid #e5e7eb;
`;

const ModalTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1f2937;
  margin: 0;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #6b7280;
  padding: 0.5rem;
  border-radius: 4px;
  transition: all 0.2s;

  &:hover {
    background: #f3f4f6;
    color: #374151;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const Label = styled.label`
  font-weight: 500;
  color: #374151;
  font-size: 0.875rem;
`;

const Input = styled.input`
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 1rem;
  transition: all 0.2s;

  &:focus {
    outline: none;
    border-color: #2563eb;
    box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
  }
`;

const Textarea = styled.textarea`
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 1rem;
  resize: vertical;
  min-height: 100px;

  &:focus {
    outline: none;
    border-color: #2563eb;
    box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
  }
`;

const Button = styled.button`
  background: #2563eb;
  color: white;
  padding: 1rem 2rem;
  border-radius: 6px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background: #1d4ed8;
  }

  &:disabled {
    background: #9ca3af;
    cursor: not-allowed;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const CancelButton = styled.button`
  background: #6b7280;
  color: white;
  padding: 1rem 2rem;
  border-radius: 6px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background: #4b5563;
  }
`;

interface AddPropertyModalProps {
  onClose: () => void;
}

const AddPropertyModal: React.FC<AddPropertyModalProps> = ({ onClose }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    propertyType: '',
    area: '',
    address: '',
    lat: '',
    lng: '',
    contact: '',
    features: ''
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // 여기에 매물 등록 로직 추가
    console.log('매물 등록:', formData);
    onClose();
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <ModalHeader>
          <ModalTitle>매물 등록</ModalTitle>
          <CloseButton onClick={onClose}>&times;</CloseButton>
        </ModalHeader>

        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label>매물 제목</Label>
            <Input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="매물 제목을 입력하세요"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>매물 설명</Label>
            <Textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="매물에 대한 상세 설명을 입력하세요"
            />
          </FormGroup>

          <FormGroup>
            <Label>매매가/임대료</Label>
            <Input
              type="text"
              name="price"
              value={formData.price}
              onChange={handleChange}
              placeholder="예: 5억원, 월세 50만원"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>매물 종류</Label>
            <Input
              type="text"
              name="propertyType"
              value={formData.propertyType}
              onChange={handleChange}
              placeholder="예: 상가, 사무실, 건물"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>면적 (평)</Label>
            <Input
              type="number"
              name="area"
              value={formData.area}
              onChange={handleChange}
              placeholder="예: 50"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>주소</Label>
            <Input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
              placeholder="매물 주소를 입력하세요"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>위도</Label>
            <Input
              type="number"
              name="lat"
              value={formData.lat}
              onChange={handleChange}
              placeholder="예: 37.5665"
              step="any"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>경도</Label>
            <Input
              type="number"
              name="lng"
              value={formData.lng}
              onChange={handleChange}
              placeholder="예: 126.9780"
              step="any"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>연락처</Label>
            <Input
              type="text"
              name="contact"
              value={formData.contact}
              onChange={handleChange}
              placeholder="연락처 정보"
            />
          </FormGroup>

          <FormGroup>
            <Label>특징</Label>
            <Textarea
              name="features"
              value={formData.features}
              onChange={handleChange}
              placeholder="매물의 특징이나 장점을 입력하세요"
            />
          </FormGroup>

          <ButtonGroup>
            <Button type="submit">매물 등록</Button>
            <CancelButton type="button" onClick={onClose}>
              취소
            </CancelButton>
          </ButtonGroup>
        </Form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default AddPropertyModal;
