  return (
    <HeaderContainer>
      <MainSection>
        <LogoSection>
          <Logo src="/logo.png" alt="PA Î°úÍ≥†" />
          <CompanyInfo>
            <CompanyName>ÌîºÏóêÏù¥</CompanyName>
            <CompanyType>Í≥µÏù∏Ï§ëÍ∞úÏÇ¨ÏÇ¨Î¨¥ÏÜå</CompanyType>
          </CompanyInfo>
        </LogoSection>

        <SearchSection>
          <WideInput placeholder="Îß§Î¨ºÎ≤àÌò∏, Ï†úÎ™© Í≤ÄÏÉâ" />
          <NarrowInput placeholder="Í±∞ÎûòÏú†Ìòï" />
          <NarrowInput placeholder="Î©¥Ï†Å" />
          <NarrowInput placeholder="Í∏àÏï°" />
          <NarrowInput placeholder="Îß§Î¨ºÏ¢ÖÎ•ò" />
          <WideInput placeholder="Ï£ºÏÜå Í≤ÄÏÉâ" />
        </SearchSection>

        <ButtonGroup>
          <Button variant="reset" onClick={() => window.location.reload()}>
            üîÑ Ï¥àÍ∏∞Ìôî
          </Button>
          <Button variant="upload" onClick={onAddProperty}>
            üì§ ÎåÄÎüâ ÏóÖÎ°úÎìú
          </Button>
        </ButtonGroup>
      </MainSection>
    </HeaderContainer>
  );
};

export default Header;
